gameforge.fortune_telling._100_sayTitle = "Velha"
gameforge.fortune_telling._10_npcChat = "Ouvi dizer que você é uma vidente."
gameforge.fortune_telling._110_say = "Você é muito jovem para estar suspirando assim! Você quer[ENTER]dar uma olhada no seu futuro? Você deve[ENTER]saber que existem alguns poderes por aí que[ENTER]podem lhe dar as respostas que você precisa[ENTER]para recuperar um pouco da sua esperança."
gameforge.fortune_telling._120_select = "Conte-me minha sorte"
gameforge.fortune_telling._130_select = "Don't tell me my fortune "
gameforge.fortune_telling._140_say = "That's right. Following your own path and trying[ENTER]to do your best is the most important thing in[ENTER]life. Farewell! "
gameforge.fortune_telling._150_select = "Sim, eu gostaria."
gameforge.fortune_telling._160_select = "Não, obrigado."
gameforge.fortune_telling._170_say = "I may be friendly, but I can't give you a[ENTER]Talisman for free. If you want to avoid bad luck[ENTER]and misfortune, you will have to pay for it.[ENTER]It costs %s Yang. "
gameforge.fortune_telling._180_say = "You're leaving? Perhaps you're right. Bad luck[ENTER]and sorrow are all a part of life. Take care and[ENTER]farewell. If you change your mind, come see me[ENTER]again. "
gameforge.fortune_telling._20_say = "O brilho nos seus olhos me diz que você é muito[ENTER]jovem. Por que você quer saber seu destino tão cedo[ENTER]em sua vida? Há uma razão específica para[ENTER]isso? O destino ainda tem muito guardado para você. Volte[ENTER]mais tarde."
gameforge.fortune_telling._30_say = "Do you really want to live your life the way the[ENTER]fortune teller told you? You shouldn't take those[ENTER]kinds of things so seriously. The most important[ENTER]thing is your attitude and the way you act. If[ENTER]both are positive, then nothing can go wrong. If[ENTER]you still want to know your fortune, come back[ENTER]tomorrow. "
gameforge.fortune_telling._40_sayTitle = "Velha Senhora"
gameforge.fortune_telling._50_say = "Como eu já lhe disse, sua previsão para[ENTER]hoje é a seguinte:"
gameforge.fortune_telling._60_say = "Bad luck will find you and you still haven't[ENTER]bought the talisman to turn away evil yet. Are[ENTER]you interested in buying my Talisman of Wealth? "
