gameforge.locale.bookworm.accept_success_say_1 = "Aqui está... o livro Milgyo!"
gameforge.locale.bookworm.accept_success_say_2 = "There's the Milgyo book! Take it to Soon in the[ENTER]village. "
gameforge.locale.bookworm.aranyeo_complete_say = "Which fool got this strange book for my husband?[ENTER]He's been staring at it the whole day, learning[ENTER]strange chants off by heart. He doesn'não consegue mais levantar um[ENTER]dedo! Eu'll break every one of his bones[ENTER]when I get my hands on him! "
gameforge.locale.bookworm.aranyeo_complete_say2 = "What's wrong? What's with the cold sweat on your[ENTER]forehead? "
gameforge.locale.bookworm.aranyeo_start_say = "Where did he go now? His attitude is getting on[ENTER]my nerves! He wastes his days reading books[ENTER]without earning a single Yang. And he's not even[ENTER]ashamed of the fact that he's such a bad role[ENTER]model for his children. "
gameforge.locale.bookworm.aranyeo_start_say2 = "Can books put food on your table or get clothes[ENTER]for you? I'll break all his bones when I get hold[ENTER]of him! "
gameforge.locale.bookworm.aranyeo_talk = "Falar."
gameforge.locale.bookworm.complete_say = "Oha! Sim, eu vejo! Eu vejo! Eu consigo ver o novo[ENTER]mundo!"
gameforge.locale.bookworm.do_you_find_the_book = "Você encontrou o livro Milgyo?"
gameforge.locale.bookworm.do_you_mean_this_book = "Você quis dizer este livro?"
gameforge.locale.bookworm.etc_shop_start_say = "He came here yesterday and asked about the Milgyo[ENTER]book. He didn't have any Yang, but he asked about[ENTER]it constantly anyway. Maybe Milgyo has got[ENTER]something to do with these brual creatures in the[ENTER]Valley of Seungryong. Dunno why he has developed[ENTER]a sudden interest in those creatures though. "
gameforge.locale.bookworm.etc_shop_start_say2 = "Você só tem que sentir pena de Aranyo. Por que[ENTER]ela aguenta ele?"
gameforge.locale.bookworm.etc_shop_talk_about_bookworm = "Sobre Soon, o acadêmico"
gameforge.locale.bookworm.order_accept = "Okay! I'm going to get it for you! "
gameforge.locale.bookworm.order_accept_answer = "Thanks! I'm counting on you. "
gameforge.locale.bookworm.order_find_book_label = "Kill Milgyo's disciple! "
gameforge.locale.bookworm.order_find_book_text_1 = "Kill Milgyo's disciple! "
gameforge.locale.bookworm.order_find_book_text_2 = "Take the Milgyo book from Milgyo's disciple. "
gameforge.locale.bookworm.order_refuse = "Isso deve ser bem difícil."
gameforge.locale.bookworm.order_refuse_answer = "Why don't you give it a try? I know you can do it! "
gameforge.locale.bookworm.order_say1 = "Do you want to hear something funny? I can[ENTER]actually claim that I've read all books in this[ENTER]world! Right now, I am studying a collection of[ENTER]holy texts. Rumour has it, that secret[ENTER]information and hints about how to become a saint[ENTER]may be found in the Milgyo book. "
gameforge.locale.bookworm.order_say2 = "Eu gostaria de ler este livro. O que você[ENTER]acha, você poderia conseguir ele para mim? Eu,[ENTER]é claro, recompensaria você!"
gameforge.locale.bookworm.order_too_late = "Someone else has already brought me the Milgyo[ENTER]book. You don't need to do it any more. "
gameforge.locale.bookworm.start_find_bookworm_label = "Vá até Soon"
gameforge.locale.bookworm.start_find_bookworm_text_1 = "Vá até Soon"
gameforge.locale.bookworm.start_find_bookworm_text_2 = "Vá até Soon e pergunte sobre o livro Milgyo."
gameforge.locale.bookworm.start_say = "Can you see the deep wisdom in this document?[ENTER]Individual words form sentences and that's how a[ENTER]wonderful story is made. You have to get used to[ENTER]to being led by other people's imaginações e[ENTER]isso's not to everyone's liking, but I love it. "
gameforge.locale.bookworm.start_say2 = "Quanto mais você tem, mais você consegue.[ENTER]Quanto mais você sabe, mais divertido você tem na vida.[ENTER]Ah, se eu pudesse ler livros minha vida inteira..."
gameforge.locale.bookworm.success_thanks = "Oh! That's it! I'm going to read it with pleasure. "
