gameforge.locale.monster_chat = {}
gameforge.locale.monster_chat_1001_1 = "Todos vão morrer!"
gameforge.locale.monster_chat_1001_2 = "Pequenos, pequenos, pequenos pedaços..."
gameforge.locale.monster_chat_1001_3 = "Não há escapatória!"
gameforge.locale.monster_chat_1001_4 = "O chão será encharcado com seu sangue. Morra!"
gameforge.locale.monster_chat_1001_5 = "You can beg, but it won't do you any good! "
gameforge.locale.monster_chat_1001_6 = "You can't hide your fear from me! "
gameforge.locale.monster_chat_1002_1 = "Morte! Morte a todas as pessoas..."
gameforge.locale.monster_chat_1002_2 = "Seus porcos miseráveis!"
gameforge.locale.monster_chat_1002_3 = "You're starting to make me angry... "
gameforge.locale.monster_chat_1002_4 = {}
gameforge.locale.monster_chat_1002_5 = "Haverá sangue, humano."
gameforge.locale.monster_chat_1003_1 = "Morte e Perdição...!"
gameforge.locale.monster_chat_1003_2 = "Você viu as feridas que uma lança pode rasgar na sua pele?"
gameforge.locale.monster_chat_1003_3 = "Yes, go on, walk away! We'll see how far you get... "
gameforge.locale.monster_chat_1003_4 = "Só de ver suas mãos tremendo já vale a pena para mim..."
gameforge.locale.monster_chat_1003_5 = "Sua escória, ousa me desafiar?"
gameforge.locale.monster_chat_1003_6 = "A morte será sua única companheira no futuro!"
gameforge.locale.monster_chat_1004_1 = "Sua alma será minha, humano!"
gameforge.locale.monster_chat_1004_2 = "Ah, sangue fresco!"
gameforge.locale.monster_chat_1004_3 = "Parece que outra pessoa vai encontrar a morte aqui!"
gameforge.locale.monster_chat_1004_4 = "Você deveria sofrer e liberar toda a sua angústia..."
gameforge.locale.monster_chat_1004_5 = "Você realmente acha que aquela arma ridícula em sua mão será capaz de proteger você?"
gameforge.locale.monster_chat_1004_6 = "Your courage brought you to me. And now it's going to leave you... "
gameforge.locale.monster_chat_1035_1 = "Se eu morrer, minha vingança te seguirá por toda a eternidade!"
gameforge.locale.monster_chat_1035_2 = "Eu farei você morrer e sofrer devagar, muito devagar..."
gameforge.locale.monster_chat_1035_3 = "Será um prazer fazer você sofrer."
gameforge.locale.monster_chat_1035_4 = "Seu corpo está tremendo como o de uma velha senil!"
gameforge.locale.monster_chat_1035_5 = "Eu posso sentir o seu medo, pequeno."
gameforge.locale.monster_chat_1035_6 = "Acho que é hora de remover algumas das suas partes do corpo."
gameforge.locale.monster_chat_1036_1 = "I'm going to send you into your own personal hell! "
gameforge.locale.monster_chat_1036_2 = "Sua dor o deixará louco, antes que você morra..."
gameforge.locale.monster_chat_1036_3 = "You can wail! You can beg! But it won't do you any good. "
gameforge.locale.monster_chat_1036_4 = "You're going to experience the worst death you can imagine... "
gameforge.locale.monster_chat_1036_5 = {}
gameforge.locale.monster_chat_1036_6 = "Seu poder está enfraquecendo. Você já está à beira da morte..."
gameforge.locale.monster_chat_1037_1 = "Você está pronto para caminhar pelos corredores da morte por toda a eternidade?"
gameforge.locale.monster_chat_1037_2 = "Você deveria se acostumar com a ideia de saudar a escuridão eterna..."
gameforge.locale.monster_chat_1037_3 = "Bem-vindo ao inferno!"
gameforge.locale.monster_chat_1037_4 = "Scream as much as you like! No one's going to hear you! "
gameforge.locale.monster_chat_1037_5 = "If you think you can kill me like this... you're more naive than I thought! "
gameforge.locale.monster_chat_1037_6 = "Not bad, but it still won't be able to save you from your death. "
gameforge.locale.monster_chat_1038_1 = "Olhe ao redor, isto é o inferno!"
gameforge.locale.monster_chat_1038_2 = "I'm going peel the skin from your bones. "
gameforge.locale.monster_chat_1038_3 = "Eu quero ouvir você choramingar antes que você morra."
gameforge.locale.monster_chat_1038_4 = "You know that there's no use hoping, don't you? "
gameforge.locale.monster_chat_1038_5 = {}
gameforge.locale.monster_chat_1038_6 = "No, you're not going to die yet. First I want to know how many sword blows your body can handle... "
gameforge.locale.monster_chat_1039_1 = "Ah, um voluntário..."
gameforge.locale.monster_chat_1039_2 = "Você realmente acha que tem a menor chance de escapar?"
gameforge.locale.monster_chat_1039_3 = "It's not the ground that'está se movendo... ele's your knees that are shaking with fear! "
gameforge.locale.monster_chat_1039_4 = "Bem-vindo ao lugar onde você buscará sua morte!"
gameforge.locale.monster_chat_1039_5 = "Posso ver o pânico em seus olhos."
gameforge.locale.monster_chat_1039_6 = "Ah, tão jovem e tão indefeso..."
gameforge.locale.monster_chat_1040_1 = "Podemos ver cada pequeno movimento que você faz."
gameforge.locale.monster_chat_1040_2 = "Com pressa? Você realmente quer morrer?"
gameforge.locale.monster_chat_1040_3 = "A morte já tomou seu lugar ao seu lado..."
gameforge.locale.monster_chat_1040_4 = "Can you hear my bell chiming? It'll be the last thing you hear! "
gameforge.locale.monster_chat_1040_5 = "I smell blood, lots of it... you won't survive much longer... "
gameforge.locale.monster_chat_1040_6 = "Você pode lutar... ou você pode deixar a morte abraçá-lo..."
gameforge.locale.monster_chat_1041_1 = "Por que você está aqui se você quer viver?"
gameforge.locale.monster_chat_1041_2 = "Ah, novos corpos para eu destruir!"
gameforge.locale.monster_chat_1041_3 = "It was your decision to come here, human! And now you'll have to live with the consequences... "
gameforge.locale.monster_chat_1041_4 = "Nossa força de combate nunca nos abandonará. Nós não podemos morrer!"
gameforge.locale.monster_chat_1041_5 = "There's no point in attacking us! You're doomed... "
gameforge.locale.monster_chat_1041_6 = "Talvez você morra de exaustão... ou de medo..."
gameforge.locale.monster_chat_1091_1 = "You have made it here.... and now I'm going to send you to your death. "
gameforge.locale.monster_chat_1091_2 = "Sacrifique sua alma e se torne um de nós!"
gameforge.locale.monster_chat_1091_3 = "Olhe para você! Fraco e lento..."
gameforge.locale.monster_chat_1091_4 = "Seu corpo e sua alma são meus!"
gameforge.locale.monster_chat_1091_5 = "Your legs are shaking! Just face it, you're not up to this... "
gameforge.locale.monster_chat_1091_6 = "Quanto tempo mais você conseguirá aguentar isso?"
gameforge.locale.monster_chat_1093_1 = "Quem poderia imaginar que existe algo pior que a morte?"
gameforge.locale.monster_chat_1093_2 = "Bem-vindo, faminto por morte."
gameforge.locale.monster_chat_1093_3 = "Você pagará por sua indiscrição. Com seu sangue e sua alma."
gameforge.locale.monster_chat_1093_4 = "Depois que eu destruir seu corpo, sua alma será minha!"
gameforge.locale.monster_chat_1093_5 = "Por quanto tempo você acha que pode lutar comigo?"
gameforge.locale.monster_chat_1093_6 = "Você me diverte. Seu medo e sua pressa são muito divertidos..."
gameforge.locale.monster_chat_11000_1 = "Todos que estão preocupados com o futuro devem ouvir!"
gameforge.locale.monster_chat_11000_10 = "Se você quer controlar os poderes da natureza e do trovão, eu recomendo o treinamento de Força Curativa!"
gameforge.locale.monster_chat_11000_11 = "Cada Guerreiro tem um jeito diferente de lutar! Concentre-se em fortalecer seu corpo para se preparar!"
gameforge.locale.monster_chat_11000_12 = "The strength of the Sura is magic. Work on your intelligence and speed but don't neglect your physical strength! "
gameforge.locale.monster_chat_11000_13 = "Use as habilidades do Ninja! Concentre-se em velocidade e força física!"
gameforge.locale.monster_chat_11000_14 = "Shamans usam feitiços de regeneração, suporte e combate. Inteligência é vital!"
gameforge.locale.monster_chat_11000_15 = "Don't forget that you're in your apprenticeship! "
gameforge.locale.monster_chat_11000_16 = "Torne-se um aprendiz se você quer treinar suas habilidades!"
gameforge.locale.monster_chat_11000_17 = "Habilidades? Você pode ascender ao nível de Mestre, o que ocorre aleatoriamente entre os Níveis 17 e 21!"
gameforge.locale.monster_chat_11000_18 = "Você quer se tornar um grão-mestre? Então você precisa de um livro de habilidade especial!"
gameforge.locale.monster_chat_11000_19 = "Assim que você tiver terminado de ler um livro de habilidade, ele desaparece!"
gameforge.locale.monster_chat_11000_2 = "Todos os Guerreiros até o Nível 5 me escutem!"
gameforge.locale.monster_chat_11000_20 = "Você quer ler outro livro de habilidade novamente? Então espere um dia."
gameforge.locale.monster_chat_11000_21 = "Melhore sua inteligência para aprimorar suas habilidades de Sura!"
gameforge.locale.monster_chat_11000_22 = "Experimente os poderes formidáveis de um Sura em combate!"
gameforge.locale.monster_chat_11000_23 = "Você sabe onde fica o Templo da Seita Esotérica? É no meio do Vale de Seungryong!"
gameforge.locale.monster_chat_11000_24 = "The spider caves appeared again! They're in the Yongbi desert! "
gameforge.locale.monster_chat_11000_25 = "Termine suas missões e use as recompensas para melhorias!"
gameforge.locale.monster_chat_11000_26 = "Pescaria? Parece ótimo! Você já tem uma Vara de Pesca e uma isca com você?"
gameforge.locale.monster_chat_11000_27 = "You're looking for a bigger hunting challenge? The portal can send you to a hunting place that corresponds to your level! "
gameforge.locale.monster_chat_11000_28 = "Cuidado com inimigos de outros reinos. Você nunca sabe quando eles podem te atacar pelas costas."
gameforge.locale.monster_chat_11000_29 = "Você quer reiniciar suas habilidades? Então vá até a Velha! Certifique-se de trazer Yang suficiente com você!"
gameforge.locale.monster_chat_11000_3 = "Veja o mestre de luta com lâminas para treinar se você quiser se tornar um Guerreiro ofensivo poderoso!"
gameforge.locale.monster_chat_11000_30 = "Aim for the master rank! It's the only way you can become a true hero! "
gameforge.locale.monster_chat_11000_31 = "Se você quiser entrar em outro campo de batalha ou cidade, procure pelos anciãos no continente."
gameforge.locale.monster_chat_11000_4 = "Se você quiser ser proficiente com as habilidades de um Guerreiro, vá aprender com o mestre da arte da guerra."
gameforge.locale.monster_chat_11000_5 = "Se você quiser se tornar um Sura capaz de usar espadas mágicas, escolha o treinamento com armas!"
gameforge.locale.monster_chat_11000_6 = "Escolha o treinamento de magia negra se você quiser se tornar hábil no uso de feitiços negros!"
gameforge.locale.monster_chat_11000_7 = "O treinamento de arquearia é reservado para Ninjas que atingiram o Nível 5!"
gameforge.locale.monster_chat_11000_8 = "Escolha o treinamento de luta com lâminas se você quiser subir nos níveis secretos dos Ninjas!"
gameforge.locale.monster_chat_11000_9 = "Se você quer controlar batalhas como o Guerreiro do Deus Dragão, escolha o treinamento da Força do Dragão!"
gameforge.locale.monster_chat_11002_1 = "Escutem, todos vocês que se preocupam com a sua saúde!"
gameforge.locale.monster_chat_11002_10 = "Se você quer controlar o poder da natureza e do trovão, inscreva-se no treinamento da Força Curativa!"
gameforge.locale.monster_chat_11002_11 = "Cada Guerreiro tem uma técnica de luta diferente. Concentre-se em fortalecer o seu corpo!"
gameforge.locale.monster_chat_11002_12 = "A força do Sura é a magia. Trabalhe sua inteligência, velocidade e força física."
gameforge.locale.monster_chat_11002_13 = "Leve suas habilidades Ninja normais para um Nível mais alto! Concentre-se na sua velocidade e força física!"
gameforge.locale.monster_chat_11002_14 = "Shamans usam feitiços de regeneração, suporte e combate durante a batalha. Inteligência é vital para eles!"
gameforge.locale.monster_chat_11002_15 = "Don't forget what you've learned! "
gameforge.locale.monster_chat_11002_16 = "Torne-se um aprendiz se você quer treinar suas habilidades!"
gameforge.locale.monster_chat_11002_17 = "Habilidades? Você pode subir até o Nível de mestre, o que ocorre aleatoriamente entre os Níveis 17 e 21!"
gameforge.locale.monster_chat_11002_18 = "Você quer se tornar um Grão-Mestre? Então você precisa de um livro de habilidade especial!"
gameforge.locale.monster_chat_11002_19 = "Assim que você terminar de ler um livro de habilidade, ele desaparece!"
gameforge.locale.monster_chat_11002_2 = "Todos os Guerreiros até o Nível 5, escutem!"
gameforge.locale.monster_chat_11002_20 = "Você quer ler outro livro de habilidade novamente? Então espere um dia."
gameforge.locale.monster_chat_11002_21 = "Aprimore sua inteligência para melhorar suas habilidades Sura!"
gameforge.locale.monster_chat_11002_22 = "Experimente os grandes poderes de um Sura em combate!"
gameforge.locale.monster_chat_11002_23 = "Você sabe onde fica o templo da seita esotérica? É no meio do Vale de Seungryong!"
gameforge.locale.monster_chat_11002_24 = "The spider caves appeared again! They're in the Yongbi desert! "
gameforge.locale.monster_chat_11002_25 = "Conclua suas missões e use as recompensas para aprimoramento!"
gameforge.locale.monster_chat_11002_26 = "Pesca? Parece ótimo! Você já tem uma vara de pesca e uma isca com você?"
gameforge.locale.monster_chat_11002_27 = "You're looking for a bigger hunting challenge? The portal can send you to a hunting place that corresponds to your level! "
gameforge.locale.monster_chat_11002_28 = "Cuidado com inimigos de outros reinos. Você nunca sabe quando eles podem apunhalar você pelas costas."
gameforge.locale.monster_chat_11002_29 = "Você quer redefinir suas habilidades? Então vá para a velha! Certifique-se de trazer Yang suficiente com você!"
gameforge.locale.monster_chat_11002_3 = "Veja o professor de combate com lâmina para treinamento se você quer ser conhecido como um poderoso Guerreiro ofensivo!"
gameforge.locale.monster_chat_11002_30 = "Aim for the master rank! It's the only way you can become a true hero! "
gameforge.locale.monster_chat_11002_31 = "Se você quer entrar em outro campo de batalha ou cidade, procure por pessoas velhas no continente."
