gameforge.locale.monster_chat_1105_1 = "Estar congelado é bom... para mim!"
gameforge.locale.monster_chat_1105_2 = "I'm sure that Mount Sohan is the best place! "
gameforge.locale.monster_chat_1105_3 = "Que clima agradável."
gameforge.locale.monster_chat_1105_4 = "No final você ficará tão frio quanto eu..."
gameforge.locale.monster_chat_1105_5 = "Você pode viver para sempre, pelo menos até o gelo derreter."
gameforge.locale.monster_chat_1105_6 = "Como você acha que vai se sentir, quando você lentamente se transformar em um pedaço de gelo?"
gameforge.locale.monster_chat_1106_1 = "Humanos...aqui?!"
gameforge.locale.monster_chat_1106_2 = "Cansado...mas acordado o suficiente para você..."
gameforge.locale.monster_chat_1106_3 = "Alguém aqui, na minha colina?"
gameforge.locale.monster_chat_1106_4 = "You don't belong here! "
gameforge.locale.monster_chat_1106_5 = "You'd better back off! "
gameforge.locale.monster_chat_1106_6 = "Forasteiro... tão sozinho?"
gameforge.locale.monster_chat_1191_1 = "It's been a long time since we've seen an experienced fighter around here. "
gameforge.locale.monster_chat_1191_2 = "Nada mais a fazer além de esperar e polir armas..."
gameforge.locale.monster_chat_1191_3 = "Devo trabalhar neles?"
gameforge.locale.monster_chat_1191_4 = {}
gameforge.locale.monster_chat_1191_5 = "Algo está faltando. Não estou satisfeito..."
gameforge.locale.monster_chat_1191_6 = "I'm going to put quick end to your pain - and it'é a última dor que você'll ever feel! "
gameforge.locale.monster_chat_1301_1 = "Um pescoçudo...!"
gameforge.locale.monster_chat_1301_2 = "Sua pele pálida ficará ainda mais pálida em um minuto."
gameforge.locale.monster_chat_1301_3 = "Você quer me desafiar? Ótimo!"
gameforge.locale.monster_chat_1301_4 = "You attacked me! You won't survive this! "
gameforge.locale.monster_chat_1301_5 = "Fraco, fraco humano!"
gameforge.locale.monster_chat_1301_6 = "Let's see how much you can bear! "
gameforge.locale.monster_chat_1302_1 = "Got away? Don't even think about it. "
gameforge.locale.monster_chat_1302_2 = "That's right... comes closer! "
gameforge.locale.monster_chat_1302_3 = "A espada está tremendo na minha mão..."
gameforge.locale.monster_chat_1302_4 = "You won't survive this encounter! "
gameforge.locale.monster_chat_1302_5 = "Lá vem minha espada!"
gameforge.locale.monster_chat_1302_6 = "Painful, isn't it? "
gameforge.locale.monster_chat_1303_1 = "Even the grass that you're walking on might kill you. "
gameforge.locale.monster_chat_1303_2 = "Eu teria cuidado se fosse você..."
gameforge.locale.monster_chat_1303_3 = "That's right. Come closer... "
gameforge.locale.monster_chat_1303_4 = "Você ousa se aproximar de mim? Bem... então você pode arcar com as consequências!"
gameforge.locale.monster_chat_1303_5 = "It's time for you say goodbye to the world! "
gameforge.locale.monster_chat_1303_6 = "Você se arrepende de ter vindo?"
gameforge.locale.monster_chat_1305_1 = "Quem pensa que pode me derrotar?"
gameforge.locale.monster_chat_1305_2 = "Você? Você quer lutar contra mim?"
gameforge.locale.monster_chat_1305_3 = "Ataque-me, se você ousar!"
gameforge.locale.monster_chat_1305_4 = "You're not thinking of running away, are you? "
gameforge.locale.monster_chat_1305_5 = "Você subestimou o alcance da minha espada."
gameforge.locale.monster_chat_1305_6 = "Você está com medo?"
gameforge.locale.monster_chat_1901_1 = "I think you're going to die at my hands today. "
gameforge.locale.monster_chat_1901_2 = "Ah, eu gosto de você."
gameforge.locale.monster_chat_1901_3 = "Ah, então você quer trazer um pouco de variedade para a minha vida?"
gameforge.locale.monster_chat_1901_4 = "Hey! It won't help you to run away like a coward. "
gameforge.locale.monster_chat_1901_5 = "Meu olhar já fez um arrepio percorrer a sua espinha?"
gameforge.locale.monster_chat_1901_6 = "Don't you want to scream a little? "
gameforge.locale.monster_chat_20001_1 = "You seem to be nervous... Don't worry, nothing is going to explode. "
gameforge.locale.monster_chat_20001_2 = "Às vezes, eu só quero passear."
gameforge.locale.monster_chat_20001_3 = "Time is going by so slowly... My grandchildren don't even visit me any more. "
gameforge.locale.monster_chat_20001_4 = "When you get old, you often don't have much company. "
gameforge.locale.monster_chat_20001_5 = "Meus melhores dias acabaram."
gameforge.locale.monster_chat_20002_1 = "O sol está tão quente."
gameforge.locale.monster_chat_20002_2 = "Wonderful weather to dry laundry, isn't it? "
gameforge.locale.monster_chat_20002_3 = "Onde está aquele marido preguiçoso e inútil?"
gameforge.locale.monster_chat_20002_4 = "He's not even ashamed that his children know about laziness! And he calls himself a father... "
gameforge.locale.monster_chat_20002_5 = "Books, always books! He doesn't go to work and doesn't bring any Yang home. He just reads the whole time! "
gameforge.locale.monster_chat_20003_1 = "Olá, pequeno, você dormiu bem?"
gameforge.locale.monster_chat_20003_2 = "Meu pequeno anjo."
gameforge.locale.monster_chat_20003_3 = "Você é meu raio de sol..."
gameforge.locale.monster_chat_20003_4 = "What's wrong? Are you hungry? "
gameforge.locale.monster_chat_20003_5 = "That's right... go to sleep, my little one. "
gameforge.locale.monster_chat_20004_1 = "Apenas mais um dia..."
gameforge.locale.monster_chat_20004_2 = "Onde vou conseguir comida hoje?"
gameforge.locale.monster_chat_20004_3 = "É sempre a mesma coisa com aqueles portadores de espada."
gameforge.locale.monster_chat_20004_4 = "Let's see. Maybe I should go to Octavio's restaurant. "
gameforge.locale.monster_chat_20004_5 = "Ah, a propósito, a guerra parece ter terminado..."
gameforge.locale.monster_chat_20005_1 = "A alma de um mestre oleiro flui por suas pontas dos dedos."
gameforge.locale.monster_chat_20005_2 = "Desde que o país foi mergulhado no caos, combatentes podem ser encontrados em todos os lugares."
gameforge.locale.monster_chat_20005_3 = "Lutas, lutas por toda parte."
gameforge.locale.monster_chat_20005_4 = "Eu rezo por um mundo calmo e pacífico."
gameforge.locale.monster_chat_20005_5 = "Por que a pressa? Tire um momento para relaxar e pensar."
gameforge.locale.monster_chat_20006_1 = "Ai meu Deus, o que devo fazer?"
gameforge.locale.monster_chat_20006_2 = "I can't find my brother anywhere, but he did promise to meet me here. "
gameforge.locale.monster_chat_20006_3 = "E agora, tudo o que vejo aqui são guerreiros armados."
gameforge.locale.monster_chat_20006_4 = "Sinto falta do meu irmão."
gameforge.locale.monster_chat_20006_5 = "Alguém viu meu irmão?"
gameforge.locale.monster_chat_20007_1 = "Ha, esta carne é realmente fresca!"
gameforge.locale.monster_chat_20007_2 = "It's good that people are eating more meat again now. "
gameforge.locale.monster_chat_20007_3 = "Os negócios estão indo muito bem, mas a carne ainda está escassa."
gameforge.locale.monster_chat_20007_4 = "Quando a carne que pedimos vai chegar?"
gameforge.locale.monster_chat_20007_5 = "If it doesn't arrive on time, we'll have to go hunting. "
gameforge.locale.monster_chat_20008_1 = "Espero que os clientes venham em massa com um tempo tão bom."
gameforge.locale.monster_chat_20008_2 = "The lazy dog will probably come around soon and he'll want something to eat. "
gameforge.locale.monster_chat_20008_3 = "Coitado!"
gameforge.locale.monster_chat_20008_4 = "Ele costumava ser um grande Guerreiro."
gameforge.locale.monster_chat_20008_5 = "I wonder why he doesn't fight any more? "
gameforge.locale.monster_chat_20009_1 = "Os jovens garotos... tão cheios de vida e energia!"
gameforge.locale.monster_chat_20009_2 = "Guerreiros parecem muito mais impressionantes quando estão empunhando armas."
gameforge.locale.monster_chat_20009_3 = "Uma mente assassina é algo que todo mundo tem hoje em dia."
gameforge.locale.monster_chat_20009_4 = "Controle seu coração, foque seus pensamentos e acalme sua alma."
gameforge.locale.monster_chat_20009_5 = "Não há receita infalível para a felicidade, mas algumas diretrizes simples que você pode seguir."
gameforge.locale.monster_chat_20010_1 = "Aqui, este é um item muito útil!"
gameforge.locale.monster_chat_20010_2 = "Mercadorias de países distantes! Peças lindas, extraordinárias!"
gameforge.locale.monster_chat_20010_3 = "Cheguem mais perto, todos!"
gameforge.locale.monster_chat_20010_4 = "Come on, people, buy! You won't have this chance again! "
gameforge.locale.monster_chat_20010_5 = "Há muitos ladrões por aqui. Eles deveriam ficar longe das minhas mercadorias!"
gameforge.locale.monster_chat_20011_1 = "A terra tem estado muito seca nos últimos anos."
gameforge.locale.monster_chat_20011_2 = "The grain harvest isn't as good as it used to be. "
gameforge.locale.monster_chat_20011_3 = "Eu me pergunto por que as sementes estão se deteriorando nos campos."
gameforge.locale.monster_chat_20011_4 = "O grão está quebradiço e tem gosto podre."
gameforge.locale.monster_chat_20011_5 = "That's a bad sign. Farming is the foundation of our lives. "
gameforge.locale.monster_chat_20012_1 = "Bolinhos de arroz! Deliciosos bolinhos de arroz!"
gameforge.locale.monster_chat_20012_2 = "Eu vendo doces bolinhos de arroz!"
gameforge.locale.monster_chat_20012_3 = "É, o negócio tem estado bem ruim ultimamente."
gameforge.locale.monster_chat_20012_4 = "Talvez eu deva tentar em outra cidade."
gameforge.locale.monster_chat_20012_5 = "Mas há tantos predadores selvagens por aqui..."
gameforge.locale.monster_chat_20013_1 = "Em dias como estes, estar em um barco é a melhor coisa que pode acontecer com você."
gameforge.locale.monster_chat_20013_2 = "Tempo perfeito para pescar."
gameforge.locale.monster_chat_20013_3 = "Agora mais um nó... Certo! Nenhum peixe vai morder este anzol!"
gameforge.locale.monster_chat_20013_4 = "Aquele maldito urso arruinou minha vara de pesca."
gameforge.locale.monster_chat_20013_5 = "Eu deveria consertar minhas redes novamente."
gameforge.locale.monster_chat_20014_1 = "Hã!"
gameforge.locale.monster_chat_20014_2 = "Don't worry about me; that's just how I am. "
gameforge.locale.monster_chat_20014_3 = "Ha!"
gameforge.locale.monster_chat_20014_4 = "O que devo fazer para que eles... Ai meu Deus, o que eu disse agora mesmo?"
gameforge.locale.monster_chat_20014_5 = "I'm just not brave enough. "
gameforge.locale.monster_chat_20015_1 = "Este machado me serviu bem por 20 anos."
gameforge.locale.monster_chat_20015_2 = "Ainda afiado, embora talvez não tão bom quanto antes."
gameforge.locale.monster_chat_20015_3 = "Eu quero continuar usando-o, então ele precisa ser afiado."
gameforge.locale.monster_chat_20015_4 = "Eu preciso de pedras minerais especiais para isso."
gameforge.locale.monster_chat_20015_5 = "Mas onde posso consegui-las?"
gameforge.locale.monster_chat_20016_1 = "Os clientes estão vindo em massa."
gameforge.locale.monster_chat_20016_2 = "Espero que eu termine tudo a tempo."
gameforge.locale.monster_chat_20016_3 = "Martelada após martelada e sem reclamar..."
gameforge.locale.monster_chat_20016_4 = "É assim que um martelo deve soar!"
gameforge.locale.monster_chat_20016_5 = "Bem quente aqui."
gameforge.locale.monster_chat_20017_1 = "I'm not in the mood for music. "
gameforge.locale.monster_chat_20017_2 = "Meu instrumento está em péssimo estado."
gameforge.locale.monster_chat_20017_3 = "Talvez eu devesse arranjar um novo instrumento?"
gameforge.locale.monster_chat_20017_4 = "Meu instrumento está velho e gasto... Eu adoro coisas antigas!"
gameforge.locale.monster_chat_20017_5 = "Preciso urgentemente encontrar um substituto para as cordas rompidas."
gameforge.locale.monster_chat_20018_1 = "I'm so busy! "
gameforge.locale.monster_chat_20018_2 = "Todos os dias mais feridos chegam aqui."
gameforge.locale.monster_chat_20018_3 = "There's no time for private amusement! "
gameforge.locale.monster_chat_20018_4 = "And we're running out of medicine. "
gameforge.locale.monster_chat_20018_5 = "Nós precisamos de alguém que consiga alguns ingredientes para o remédio."
gameforge.locale.monster_chat_20019_1 = "Há 30 anos, eu vago pelas montanhas com meu arco."
gameforge.locale.monster_chat_20019_2 = "Sustentei minha esposa com isso e criei três filhos."
gameforge.locale.monster_chat_20019_3 = "It's a hard life but worth it. "
gameforge.locale.monster_chat_20019_4 = "But now, the bowstring has ripped and I can't hunt any more. I can't feed my family like this! "
gameforge.locale.monster_chat_20019_5 = "If I can't repair this bow, I'terei que comprar um novo. Mas eu não't have enough money to do that. "
gameforge.locale.monster_chat_20020_1 = "Ei, o que você está encarando?"
gameforge.locale.monster_chat_20020_2 = "Fique longe de mim!"
gameforge.locale.monster_chat_20020_3 = "Por quanto tempo terei que continuar me escondendo?"
gameforge.locale.monster_chat_20020_4 = "Morrer sozinho e esquecido pelo resto do mundo não é realmente o que eu quero."
gameforge.locale.monster_chat_20020_5 = "Alguém tem que me ajudar!"
gameforge.locale.monster_chat_20021_1 = "Este dia vai acabar como qualquer outro dia."
gameforge.locale.monster_chat_20021_2 = "O que posso fazer agora?"
gameforge.locale.monster_chat_20021_3 = "Nunca pensei que terminaria assim."
gameforge.locale.monster_chat_20021_4 = "I still can't believe that he's not here any more. "
gameforge.locale.monster_chat_20021_5 = "He didn't deserve that. "
gameforge.locale.monster_chat_20022_1 = "Mais um ótimo dia."
gameforge.locale.monster_chat_20022_2 = "Ariyoung's husband has disappeared. "
gameforge.locale.monster_chat_20022_3 = "Eu queria ajudar, mas essa teimosia..."
gameforge.locale.monster_chat_20022_4 = "Se ela tivesse se casado comigo, poderia ter deixado sua vida chata para trás."
gameforge.locale.monster_chat_20022_5 = "Talvez eu devesse ter expressado meus sentimentos por ela melhor."
gameforge.locale.monster_chat_20023_1 = "Ah não! O que devo fazer se minha esposa descobrir?"
gameforge.locale.monster_chat_20023_2 = "Se eu pudesse viver apenas lendo livros!"
gameforge.locale.monster_chat_20023_3 = "Todo livro leva você para seu próprio mundo misterioso."
gameforge.locale.monster_chat_20023_4 = "I know that I can't live like this forever, but... "
gameforge.locale.monster_chat_20023_5 = "Eu gostaria de poder mudar isso pela minha esposa, mas..."
gameforge.locale.monster_chat_20024_1 = "Vamos lá, coloque um pouco de esforço nisso!"
gameforge.locale.monster_chat_20024_2 = "What are you doing? Can't you hurry up a little more? "
gameforge.locale.monster_chat_20024_3 = "Imagine um Guerreiro erguendo sua espada e faça o mesmo!"
gameforge.locale.monster_chat_20024_4 = "Onde está aquele tolo agora?"
gameforge.locale.monster_chat_20024_5 = "Você quer ser um homem?"
gameforge.locale.monster_chat_2091_1 = "Corte, corte... cabelo, gargantas..."
gameforge.locale.monster_chat_2091_2 = "Eu tenho algo para você: sono eterno!"
gameforge.locale.monster_chat_2091_3 = "Take a deep breath. Don't worry, it's only poison! "
gameforge.locale.monster_chat_2091_4 = "I'll only leave your face! That'll make a nice decoration... "
gameforge.locale.monster_chat_2091_5 = {}
gameforge.locale.monster_chat_2091_6 = "Wrapped up in silk you'll make a nice appetiser for later... "
gameforge.locale.monster_chat_2092_1 = "There aren't many of us left. "
gameforge.locale.monster_chat_2092_2 = "Eles não são as criaturas de duas pernas que deveriam governar aqui."
gameforge.locale.monster_chat_2092_3 = "Hum, parece que alguém se prendeu na minha teia de aranha."
gameforge.locale.monster_chat_2092_4 = "I am a peaceful person. I just don't like being irritated. "
gameforge.locale.monster_chat_2092_5 = "É só isso que você pode oferecer?"
gameforge.locale.monster_chat_2092_6 = "Ninguém pode se comparar a mim!"
gameforge.locale.monster_chat_2104_1 = "Por que as pessoas são tão molhadas por dentro? Isso faz minha espada enferrujar!"
gameforge.locale.monster_chat_2104_2 = "O veneno no seu ferrão já secou?"
gameforge.locale.monster_chat_2104_3 = "Então, e a luta agora?"
gameforge.locale.monster_chat_2104_4 = "Já exausto pelo calor?"
gameforge.locale.monster_chat_2104_5 = "Eu gosto de deixar as pessoas para trás no deserto."
gameforge.locale.monster_chat_2104_6 = "Pobre você, enterrado no deserto assim sem nenhum tipo de cerimônia..."
gameforge.locale.monster_chat_2105_1 = "I'm going to tell you a secret - I hate dirt! "
gameforge.locale.monster_chat_2105_2 = "It's quite hot, isn't it? "
gameforge.locale.monster_chat_2105_3 = "Este lugar é perigoso."
gameforge.locale.monster_chat_2105_4 = {}
gameforge.locale.monster_chat_2105_5 = "I'm going to hit you right between the eyes. "
gameforge.locale.monster_chat_2106_1 = "It seems like there's something around here for me to mow down. "
gameforge.locale.monster_chat_2106_2 = "Ah sim, consigo ver minha próxima presa."
gameforge.locale.monster_chat_2106_3 = "Gosto disso, gosto muito disso!"
gameforge.locale.monster_chat_2106_4 = "That one there is nice and fat. That's mine! "
gameforge.locale.monster_chat_2106_5 = {}
gameforge.locale.monster_chat_2106_6 = "Amo o cheiro de sangue."
gameforge.locale.monster_chat_2107_1 = "Não há muito para matar recentemente."
gameforge.locale.monster_chat_2107_2 = "Ei, esses estão bem gordos!"
gameforge.locale.monster_chat_2107_3 = "O líder vai adorar isso..."
gameforge.locale.monster_chat_2107_4 = {}
gameforge.locale.monster_chat_2107_5 = "Let's put on our dancing shoes! "
gameforge.locale.monster_chat_2107_6 = "Esta flecha é só para você."
gameforge.locale.monster_chat_2108_1 = "If I get one, I'll... "
gameforge.locale.monster_chat_2108_2 = "Hey, what's wrong? "
gameforge.locale.monster_chat_2108_3 = "Você acha que sou facilmente derrotado?"
gameforge.locale.monster_chat_2108_4 = "Que pena que você teve que esbarrar em mim no deserto!"
gameforge.locale.monster_chat_2108_5 = "Ah, essa sensação; vou aproveitar isso para sempre!"
gameforge.locale.monster_chat_2108_6 = "Obrigado!"
gameforge.locale.monster_chat_2191_1 = "Sinto cheiro de humanos. Que cheiro delicioso!"
gameforge.locale.monster_chat_2191_2 = "That's right, come here... "
gameforge.locale.monster_chat_2191_3 = {}
gameforge.locale.monster_chat_2191_4 = "I'm so lucky that you got lost and found your way here! "
gameforge.locale.monster_chat_2191_5 = "Pare!"
gameforge.locale.monster_chat_2191_6 = "Bit by bit I'm going to rip it out of your flesh... "
gameforge.locale.monster_chat_2202_1 = "Chegue mais perto para que eu possa queimar a sua pele."
gameforge.locale.monster_chat_2202_2 = "Você sabe que tipo de dor tamanha quantidade de calor pode causar?"
gameforge.locale.monster_chat_2202_3 = "Talvez eu deva esfriar minhas chamas com um pouco de sangue fresco."
gameforge.locale.monster_chat_2202_4 = "Seu sangue... deixe-me prová-lo."
gameforge.locale.monster_chat_2202_5 = "There's no point in putting up a fight. "
gameforge.locale.monster_chat_2202_6 = "Torne-se como eu! Apenas chamas e calor."
gameforge.locale.monster_chat_2204_1 = "As long as I'm here, no one will get through here! "
gameforge.locale.monster_chat_2204_2 = "For some reason, you're starting to annoy me. "
gameforge.locale.monster_chat_2204_3 = {}
gameforge.locale.monster_chat_2204_4 = "Você sabe onde você está?"
gameforge.locale.monster_chat_2204_5 = {}
gameforge.locale.monster_chat_2204_6 = "Você é um oponente bastante patético para mim!"
gameforge.locale.monster_chat_2205_1 = "Uma espada só é útil nas mãos de um Guerreiro."
gameforge.locale.monster_chat_2205_2 = "Mantenha a espada na sua mão."
gameforge.locale.monster_chat_2205_3 = "A guerra está chegando."
gameforge.locale.monster_chat_2205_4 = "E agora lutamos até a morte!"
gameforge.locale.monster_chat_2205_5 = "Você não pode resistir à morte por mais tempo!"
gameforge.locale.monster_chat_2205_6 = "Você vai morrer lentamente."
gameforge.locale.monster_chat_2206_1 = "Lava é o elixir da vida."
gameforge.locale.monster_chat_2206_2 = "A lava me mantém vivo e será a sua morte!"
gameforge.locale.monster_chat_2206_3 = "Isn't it wonderful, this world of glowing lava? "
gameforge.locale.monster_chat_2206_4 = "Você ousa me incomodar?"
gameforge.locale.monster_chat_2206_5 = "Você deveria ir embora se você valoriza a sua vida."
gameforge.locale.monster_chat_2206_6 = "Você pode se arrepender da minha presença aqui."
gameforge.locale.monster_chat_2291_1 = "It's a nice day. All I want to do is have some peace and quiet. "
gameforge.locale.monster_chat_2291_2 = "Nada se compara ao país de Doyyumhwan."
gameforge.locale.monster_chat_2291_3 = "Um pouco de sangue fresco seria uma mudança bem-vinda."
gameforge.locale.monster_chat_2291_4 = "You seem to be seeking your death, otherwise you wouldn't be disturbing me now.  "
gameforge.locale.monster_chat_2291_5 = "Você já está se arrependendo de ter aparecido?"
gameforge.locale.monster_chat_2291_6 = "It's too late to run away. Face your fate. "
gameforge.locale.monster_chat_301_1 = "Estes tempos tensos estão me deixando nervoso."
gameforge.locale.monster_chat_301_2 = "Dois dias atrás, uma jovem garota foi atacada por animais selvagens na área."
gameforge.locale.monster_chat_301_3 = "Os animais selvagens estão ficando cada vez mais agressivos. Deveríamos tentar matar o máximo possível."
gameforge.locale.monster_chat_301_4 = "I'm not in a good mood today. Strangers should best keep out of my way. "
gameforge.locale.monster_chat_301_5 = "O último guerreiro que eu matei, implorou por sua vida."
gameforge.locale.monster_chat_302_1 = "Alguém precisa fazer algo sobre estas pedras."
gameforge.locale.monster_chat_302_2 = "I don't trust the peacefulness of the morning. "
gameforge.locale.monster_chat_302_3 = "A tensão no ar está me deixando desconfortável."
gameforge.locale.monster_chat_302_4 = "Estas pontas de flecha foram muito bem forjadas."
gameforge.locale.monster_chat_302_5 = "If I were you I'd be careful. That arrow is aiming straight for your heart. "
gameforge.locale.monster_chat_302_6 = "Eu deveria ir lá e matar uma daquelas feras."
gameforge.locale.monster_chat_303_1 = "Um bom treinamento de artes marciais é essencial nestes tempos."
gameforge.locale.monster_chat_303_2 = "Os soldados não conseguem mais controlar o caos no país."
gameforge.locale.monster_chat_303_3 = "Por que você está olhando para mim desse jeito?"
gameforge.locale.monster_chat_303_4 = "Covardes têm uma pequena chance de sobreviver."
gameforge.locale.monster_chat_303_5 = "Don't overestimate yourself. "
gameforge.locale.monster_chat_303_6 = "Alguém parece ter treinado você muito bem."
gameforge.locale.monster_chat_304_1 = "Até meus ancestrais conseguiram se impor em batalha nestes territórios."
gameforge.locale.monster_chat_304_2 = "Você tem que estar alerta e ser habilidoso aqui."
gameforge.locale.monster_chat_304_3 = "Don't even think about wasting my time. "
gameforge.locale.monster_chat_304_4 = "Então, como é saber que sua vida está em perigo?"
gameforge.locale.monster_chat_304_5 = "Você já quer desistir?"
gameforge.locale.monster_chat_304_6 = "Prepare-se para sofrer."
gameforge.locale.monster_chat_305_1 = "Nossa! Essa espada é fantástica."
gameforge.locale.monster_chat_305_2 = "Seja mais persistente e aumente seu poder!"
gameforge.locale.monster_chat_305_3 = "Você acha que pode se tornar tão forte quanto eu?"
gameforge.locale.monster_chat_305_4 = "Okay. Let's get serious. "
gameforge.locale.monster_chat_305_5 = "Oh my! I'm just a weak woman! "
gameforge.locale.monster_chat_305_6 = "You look tired. Why don't you give up already? Hehe. "
gameforge.locale.monster_chat_306_1 = "O que Serangyi vai fazer hoje, hein?"
gameforge.locale.monster_chat_306_2 = "Estou tão entediado. Brinque comigo!"
gameforge.locale.monster_chat_306_3 = "Tenho que dizer que gosto mais do Sura."
gameforge.locale.monster_chat_306_4 = "Ei! Que tal uma luta?"
gameforge.locale.monster_chat_306_5 = "Don't underestimate me just because I'm a woman! "
gameforge.locale.monster_chat_306_6 = "A dor dura apenas um momento..."
gameforge.locale.monster_chat_307_1 = "Quero sair daqui, a todo custo."
gameforge.locale.monster_chat_307_2 = "Quão seguras são as casas de cada membro?"
gameforge.locale.monster_chat_307_3 = "Calem a boca, meninas!"
gameforge.locale.monster_chat_307_4 = "Deixe-me derrotar você. Com um único golpe."
gameforge.locale.monster_chat_307_5 = "Don't even think about running away! "
gameforge.locale.monster_chat_307_6 = "So, why don't we get started? "
gameforge.locale.monster_chat_394_1 = "Don't forget: All men are enemies. "
gameforge.locale.monster_chat_394_2 = "Se uma pessoa tem um rosto bonito, podemos abrir uma exceção..."
gameforge.locale.monster_chat_394_3 = "O quê, você quer lutar?"
gameforge.locale.monster_chat_394_4 = "It won't take long, but it will hurt. "
gameforge.locale.monster_chat_394_5 = "Ah, você realmente parece estar com dor...?"
gameforge.locale.monster_chat_394_6 = "Adeus! Agora vá para o inferno!"
gameforge.locale.monster_chat_401_1 = "Então, o que você faz no Juramento Branco?"
gameforge.locale.monster_chat_401_2 = "Aonde você vai?"
gameforge.locale.monster_chat_401_3 = "Ei, você quer lutar?"
gameforge.locale.monster_chat_401_4 = "Hehe! You're dodging my hits...? "
gameforge.locale.monster_chat_401_5 = "Você acha que pode fugir de mim?"
gameforge.locale.monster_chat_401_6 = "Lá vem meu golpe!"
gameforge.locale.monster_chat_402_1 = "Meu Deus, estou tão cansado!"
gameforge.locale.monster_chat_402_2 = "Ei, que armadura legal! Custou caro?"
gameforge.locale.monster_chat_402_3 = "Mas pelo menos temos um tempo bom."
gameforge.locale.monster_chat_402_4 = "Ah, você treinou muito! Ótimo!"
gameforge.locale.monster_chat_402_5 = "O que você acha das minhas habilidades? Boas, hein? Ha ha!"
gameforge.locale.monster_chat_402_6 = "What's up? Oh, you're irritated by my incredible speediness! "
gameforge.locale.monster_chat_403_1 = "Why don't I go hunting? "
gameforge.locale.monster_chat_403_2 = "Ninguém atacou nossa aldeia hoje."
gameforge.locale.monster_chat_403_3 = "Se você quer lutar, então lute, seu bastardo!"
gameforge.locale.monster_chat_403_4 = "Hehe, você é apenas um iniciante!"
gameforge.locale.monster_chat_403_5 = "O que devo atirar primeiro, no seu braço ou na sua perna?"
gameforge.locale.monster_chat_403_6 = "Aqui vem minha flecha!"
gameforge.locale.monster_chat_404_1 = "Eu tenho que ir para a aldeia e treinar."
gameforge.locale.monster_chat_404_2 = "Me sinto tão travado."
gameforge.locale.monster_chat_404_3 = "What's for dinner today? "
gameforge.locale.monster_chat_404_4 = "I'm watching, so scream as much as you want. "
gameforge.locale.monster_chat_404_5 = "Esperou demais? Que tal balançar essa sua espada!"
gameforge.locale.monster_chat_404_6 = "Essa espada tem um som tão bom!"
gameforge.locale.monster_chat_405_1 = "Essa espada está pronta!"
gameforge.locale.monster_chat_405_2 = "Hmm, let's see: Who should this sword kill first... "
gameforge.locale.monster_chat_405_3 = "Ei, o que você está olhando?"
gameforge.locale.monster_chat_405_4 = "Primeiro, sua barriga!"
gameforge.locale.monster_chat_405_5 = "I'd like to use you as a sheath. "
gameforge.locale.monster_chat_405_6 = "I'd like to use you as a sheath. "
gameforge.locale.monster_chat_406_1 = "I'm starving. "
gameforge.locale.monster_chat_406_2 = "Tem algo para comer?"
gameforge.locale.monster_chat_406_3 = "Por que ainda estou com fome depois de toda essa comida?"
gameforge.locale.monster_chat_406_4 = "Aaargh, morra!"
gameforge.locale.monster_chat_406_5 = "Meu estômago vai decidir essa luta!"
gameforge.locale.monster_chat_406_6 = "It's the choice between heaven and hell! "
gameforge.locale.monster_chat_491_1 = "I can't stand it anymore! My body is itching everywhere! "
gameforge.locale.monster_chat_491_2 = "Ah, sim. Eu vejo alguns porcos!"
gameforge.locale.monster_chat_491_3 = "No chão já?"
gameforge.locale.monster_chat_491_4 = "I'll finish you with one hit. "
gameforge.locale.monster_chat_491_5 = "That's going to be a great memory! "
gameforge.locale.monster_chat_491_6 = "Morra!"
gameforge.locale.monster_chat_492_1 = "Meu corpo amoleceu."
gameforge.locale.monster_chat_492_2 = "Why don't I train more? "
gameforge.locale.monster_chat_492_3 = "Com medo, né?"
gameforge.locale.monster_chat_492_4 = "Ah, mais uma vítima da minha arma poderosa."
gameforge.locale.monster_chat_492_5 = "Você consegue ver meu padrão de ataque? Não? Azar o seu!"
gameforge.locale.monster_chat_492_6 = "Até mesmo pequenos cortes podem influenciar sua saúde. Principalmente se eles vieram de uma maça!"
gameforge.locale.monster_chat_493_1 = "Droga, essas mãos são realmente impráticas."
gameforge.locale.monster_chat_493_2 = "O que você está encarando? Nunca viu alguém com um punho de ferro?"
gameforge.locale.monster_chat_493_3 = "Vocês realmente me irritam."
gameforge.locale.monster_chat_493_4 = "Do you want me to beat you up? If you don't then run away. Got it? "
gameforge.locale.monster_chat_493_5 = "Now watch, I'm going to show you my big hit! "
gameforge.locale.monster_chat_493_6 = "Pare de encarar minha mão!"
gameforge.locale.monster_chat_494_1 = "Ei, você aí! Você quer lutar? Então venha aqui!"
gameforge.locale.monster_chat_494_2 = "I think I'm pretty breath-taking... "
gameforge.locale.monster_chat_494_3 = "Why don't we plan something? "
gameforge.locale.monster_chat_494_4 = "Não, você não pode sair ainda. Lute!"
gameforge.locale.monster_chat_494_5 = "Ha, aí está!"
gameforge.locale.monster_chat_494_6 = "You're dead meat! "
gameforge.locale.monster_chat_501_1 = "Ei, fique aqui! Jogue comigo!"
gameforge.locale.monster_chat_501_2 = "Eu poderia esculpir algo bom com seus ossos..."
gameforge.locale.monster_chat_501_3 = "We haven't trained yet today. "
gameforge.locale.monster_chat_501_4 = "Wow, that's great armour! I want to have it. "
gameforge.locale.monster_chat_501_5 = "De agora em diante, você terá dificuldade em ignorar meus ataques!"
gameforge.locale.monster_chat_501_6 = "Ha, ha, ha! Bet you didn't expect that! "
gameforge.locale.monster_chat_502_1 = "Beauty isn't everything. "
gameforge.locale.monster_chat_502_2 = "Don't stare, fight! "
gameforge.locale.monster_chat_502_3 = "I don't like the way that you are staring at me. Come here! "
gameforge.locale.monster_chat_502_4 = "It's an honour to die at my sword! "
gameforge.locale.monster_chat_502_5 = {}
gameforge.locale.monster_chat_502_6 = "Excelente!"
gameforge.locale.monster_chat_503_1 = "Hey there, don't touch! "
gameforge.locale.monster_chat_503_2 = "Parece que minhas cordas do arco foram cortadas."
gameforge.locale.monster_chat_503_3 = "Droga, aqueles idiotas estavam aqui."
gameforge.locale.monster_chat_503_4 = "Nada mal. Mas você vai morrer de qualquer jeito."
gameforge.locale.monster_chat_503_5 = "You're okay with this? That's great! "
gameforge.locale.monster_chat_503_6 = "Lá vem a flecha!"
gameforge.locale.monster_chat_504_1 = "Venham todos para cá!"
gameforge.locale.monster_chat_504_2 = "La, lala, I'm whistling a song... "
gameforge.locale.monster_chat_504_3 = "Que tempo fantástico!"
gameforge.locale.monster_chat_504_4 = {}
gameforge.locale.monster_chat_504_5 = "Ótimo! Eu aprecio seu espírito de luta!"
gameforge.locale.monster_chat_504_6 = "Venham aqui! Todos para mim!"
gameforge.locale.monster_chat_601_1 = "I'm starving! "
gameforge.locale.monster_chat_601_2 = "Estou com vontade de umas frutas."
gameforge.locale.monster_chat_601_3 = "Bem, ok, peixe serve. Ataque!"
gameforge.locale.monster_chat_601_4 = "Hey, don't run away! Stop! "
gameforge.locale.monster_chat_601_5 = "I won. It's obvious! "
gameforge.locale.monster_chat_601_6 = "Você viu? Você errou, haha!"
gameforge.locale.monster_chat_601_7 = "Você devia tomar cuidado, haha!"
gameforge.locale.monster_chat_602_1 = "Onde estou?"
gameforge.locale.monster_chat_602_2 = "Tempo bom hoje."
gameforge.locale.monster_chat_602_3 = "You came to hunt me down? That's ridiculous! "
gameforge.locale.monster_chat_602_4 = "Você é minha próxima vítima."
gameforge.locale.monster_chat_602_5 = "Quite painful isn't it? Well, I'll make it quick. "
gameforge.locale.monster_chat_602_6 = "Olha como sou forte: consigo balançar este enorme porrete!"
gameforge.locale.monster_chat_603_1 = "Que chato!"
gameforge.locale.monster_chat_603_2 = "Meu corpo inteiro está coçando."
gameforge.locale.monster_chat_603_3 = "Let's see who's faster. "
gameforge.locale.monster_chat_603_4 = "You started it. So don't make me responsible for your death! "
gameforge.locale.monster_chat_603_5 = "Once you're in, you won'não consegue mais sair. Não tem's no escape! "
gameforge.locale.monster_chat_603_6 = "Here comes the last hit. I'll finish you! "
gameforge.locale.monster_chat_604_1 = "I've made a yummy new poison. Would you like a taste? "
gameforge.locale.monster_chat_604_2 = "Eu quero ver como funciona! Aqui, pegue!"
gameforge.locale.monster_chat_604_3 = "Ei você aí! Venha aqui, você quer ser minha cobaia?"
gameforge.locale.monster_chat_604_4 = "I'll poison you! "
gameforge.locale.monster_chat_604_5 = "Ah, muito bem! Uma cobaia!"
gameforge.locale.monster_chat_604_6 = "It isn't any fun if you give up that quickly! Come on, defend yourself! "
gameforge.locale.monster_chat_631_1 = "Wait a moment. I'm looking for something to hunt. "
gameforge.locale.monster_chat_631_2 = "It's no use if you move that slowly. Move! "
gameforge.locale.monster_chat_631_3 = "Cara, estou com tanta fome!"
gameforge.locale.monster_chat_631_4 = "It's great that I've found something to hunt! "
gameforge.locale.monster_chat_631_5 = "Let all hope of survival go! You won't survive me! "
gameforge.locale.monster_chat_631_6 = "Now you'll find out what real pain feels like! "
gameforge.locale.monster_chat_632_1 = "Que chato!"
gameforge.locale.monster_chat_632_2 = "I wonder if there's anything we can use as bait? "
gameforge.locale.monster_chat_632_3 = "Venha lutar se você quiser!"
gameforge.locale.monster_chat_632_4 = "You want to be a warrior? Then show me what you've got! "
gameforge.locale.monster_chat_632_5 = {}
gameforge.locale.monster_chat_632_6 = "Fuja para a aldeia se você quiser viver!"
gameforge.locale.monster_chat_633_1 = "Morrer de fome é uma morte lastimável."
gameforge.locale.monster_chat_633_2 = "Oh, they're coming straight towards us! "
gameforge.locale.monster_chat_633_3 = "Que tal um duelo?"
gameforge.locale.monster_chat_633_4 = "Don't compare me to those bastards! "
gameforge.locale.monster_chat_633_5 = "Aqui vem o meu golpe trovão! Ha!"
gameforge.locale.monster_chat_633_6 = "Aqueles que fogem agora não são verdadeiros guerreiros!"
gameforge.locale.monster_chat_634_1 = "Desta vez, o veneno deve funcionar melhor!"
gameforge.locale.monster_chat_634_2 = "When you're hungry, bats are a real delicacy! "
gameforge.locale.monster_chat_634_3 = "Que tal uma luta?"
gameforge.locale.monster_chat_634_4 = "Once my poison enters your body, there's no cure any more. "
gameforge.locale.monster_chat_634_5 = "Minha magia vai queimar tudo, hehehe!"
gameforge.locale.monster_chat_634_6 = "Queime! Ha, ha!"
gameforge.locale.monster_chat_635_1 = "Nunca se esqueça de afiar sua espada! A próxima luta é mais cedo do que você pensa."
gameforge.locale.monster_chat_635_2 = "Sentindo-se fraco?"
gameforge.locale.monster_chat_635_3 = "Morra, e acorde na vida após a morte!"
gameforge.locale.monster_chat_635_4 = "Me derrotar? Você tem certeza?"
gameforge.locale.monster_chat_635_5 = "Lute como um homem!"
gameforge.locale.monster_chat_635_6 = "Vamos lá, só mais um pouco."
