gameforge.locale.monster_chat_637_2 = "Vá dormir. Para sempre!"
gameforge.locale.monster_chat_637_3 = "Você ainda não quer dormir?"
gameforge.locale.monster_chat_637_4 = "There's so much to do! Let's get on with it! "
