gameforge.locale.monster_chat_691_5 = "Let's fight. "
gameforge.locale.monster_chat_691_6 = "Até a morte!"
gameforge.locale.monster_chat_701_1 = "Olha, uma borboleta! Que linda!"
gameforge.locale.monster_chat_701_2 = "Don't you see the truth of heaven and earth? "
gameforge.locale.monster_chat_701_3 = "Mais tarde será tarde demais. Você tem que acreditar agora!"
