gameforge.locale.monster_chat_701_5 = "Aqueles que duvidam devem morrer!"
gameforge.locale.monster_chat_701_6 = "Alcance a iluminação abraçando a morte!"
gameforge.locale.monster_chat_702_1 = "A verdade sobre a crença esotérica reside na compreensão da ordem celestial."
gameforge.locale.monster_chat_702_2 = "A filosofia da crença esotérica é a ordem celestial!"
gameforge.locale.monster_chat_702_3 = "Verdade esotérica; verdade do Deus Tigre; Sabaha Sabaha."
gameforge.locale.monster_chat_702_4 = "Você ousa duvidar da verdade do céu e da terra?"
gameforge.locale.monster_chat_702_5 = {}
gameforge.locale.monster_chat_702_6 = "Sinta minhas garras!"
gameforge.locale.monster_chat_703_1 = "Quando a verdade do Deus Tigre for cantada pela eternidade, todo o sofrimento terá fim."
gameforge.locale.monster_chat_703_2 = "A verdade sobre a crença esotérica reside na compreensão da ordem celestial."
gameforge.locale.monster_chat_703_3 = "Sabaha Sabaha. Envie-nos conhecimento!"
gameforge.locale.monster_chat_703_4 = "Don't doubt it! "
gameforge.locale.monster_chat_703_5 = "Receba o trovão dos céus!"
gameforge.locale.monster_chat_703_6 = "Judgement's blow! "
gameforge.locale.monster_chat_704_1 = "A verdade sobre a crença esotérica reside na compreensão da ordem do céu."
gameforge.locale.monster_chat_704_2 = "Verdade esotérica; verdade do Deus Tigre; Sabaha Sabaha."
gameforge.locale.monster_chat_704_3 = "You think I'm a clown don'não é? Você não vai't for long! "
gameforge.locale.monster_chat_704_4 = "O líder da nossa seita é um santo e está acima de todos os seres vivos!"
gameforge.locale.monster_chat_704_5 = "Queime nas chamas do inferno!"
gameforge.locale.monster_chat_704_6 = "Bet you've never seen such a large tuning fork before, eh? "
gameforge.locale.monster_chat_705_1 = "Verdade esotérica; verdade do Deus Tigre; Sabaha Sabaha."
gameforge.locale.monster_chat_705_2 = "A verdade sobre a crença esotérica reside na compreensão da ordem do céu."
gameforge.locale.monster_chat_705_3 = "Just because I'm smaller than my brother, it doesn'não significa que eu'm going to put up with everything! "
gameforge.locale.monster_chat_705_4 = "Dor e escuridão eternas esperam por você!"
gameforge.locale.monster_chat_705_5 = {}
gameforge.locale.monster_chat_705_6 = "This isn't the end. On the contrary: It can only get better. "
gameforge.locale.monster_chat_706_1 = "Tudo o que pedimos é paz e privacidade! Por favor!"
gameforge.locale.monster_chat_706_2 = "Se o medo o dominar, deposite sua confiança no Deus Tigre."
gameforge.locale.monster_chat_706_3 = "A verdade da fé esotérica reside na ordem e compreensão do céu."
gameforge.locale.monster_chat_706_4 = "Let's all go to hell! "
gameforge.locale.monster_chat_706_5 = "I won't let you go! "
gameforge.locale.monster_chat_706_6 = "I'll give you everything I have. "
gameforge.locale.monster_chat_707_1 = "Eu imploro a você, por favor, salve-nos!"
gameforge.locale.monster_chat_707_2 = "A verdade sobre a crença esotérica reside em compreender a ordem do céu."
gameforge.locale.monster_chat_707_3 = "Verdade esotérica; verdade do Deus Tigre; Sabaha Sabaha."
gameforge.locale.monster_chat_707_4 = "Os portões para o inferno estão escancarados. Entre!"
gameforge.locale.monster_chat_707_5 = "I'm warning you: Don't force me to use the board! "
gameforge.locale.monster_chat_707_6 = "Eu vi que você vai morrer!"
gameforge.locale.monster_chat_791_1 = "Deixe-me pensar: Como posso resumir a ordem do céu e da terra em poucas palavras?"
gameforge.locale.monster_chat_791_2 = "Consigo ouvir os cânticos dos crentes."
gameforge.locale.monster_chat_791_3 = "That's right! Be full of remorse, and you will be rescued! "
gameforge.locale.monster_chat_791_4 = "Você sofrerá dor eterna, porque você continua a questionar a tradição."
gameforge.locale.monster_chat_791_5 = "Deixe-me ensinar você."
gameforge.locale.monster_chat_791_6 = "Don't regard it as pain - see it as a lesson. "
gameforge.locale.monster_chat_901_1 = "Eu posso não ser bonita, mas eu me divirto muito, hihihi!"
gameforge.locale.monster_chat_901_2 = "Você tem que se tornar como nós!"
