gameforge.locale.old_pirate.baekgo_order_find_virus_label = "Mate todas as pessoas infectadas."
gameforge.locale.old_pirate.baekgo_order_find_virus_text_1 = "Mate todos que foram infectados pela[ENTER]doença misteriosa!"
gameforge.locale.old_pirate.baekgo_order_find_virus_text_2 = "Kill everyone who has been infected with the[ENTER]mysterious disease, and take a sample of the[ENTER]pathogen to Baek-Go. He's going to make medicine[ENTER]for the old pirate. "
gameforge.locale.old_pirate.baekgo_order_too_late = "Baek-Go:[ENTER]Outra pessoa já entregou uma amostra do[ENTER]patógeno. Enviei o remédio para o velho pirata;[ENTER]ele deve estar bem agora."
gameforge.locale.old_pirate.baekgo_start_say = "Baek-Go:[ENTER]Ele teve contato com uma pessoa infectada apenas uma vez,[ENTER]e sua ferida começou a coçar e supurar?"
gameforge.locale.old_pirate.baekgo_start_say2 = "Baek-Go:[ENTER]Oh my! I'm quite sure he is infected with this[ENTER]mysterious disease! If he isn't treated, the[ENTER]disease will spread across the whole village! "
gameforge.locale.old_pirate.baekgo_start_say3 = "Baek-Go:[ENTER]He mustn't infect anybody! You have to treat him[ENTER]immediately. For that we need a sample of the[ENTER]virus of the mysterious illness. You can get this[ENTER]sample from a sick person. There are lots of them[ENTER]on Mount Sohan in the North. "
gameforge.locale.old_pirate.baekgo_success_say = "Baek-Go:[ENTER]You did it! Wait a moment, I'll make the medicine[ENTER]right now. [DELAY value;1000] [/DELAY][ENTER][ENTER]I was in a hurry, so I don'não sei se isso'll[ENTER]really work, but he should at least feel better.[ENTER]Take the medicine to him as fast as possible. "
gameforge.locale.old_pirate.baekgo_talk = "O Velho Pirata me enviou."
gameforge.locale.old_pirate.baekgo_talk_about_old_pirate = "The Old Pirate's illness "
gameforge.locale.old_pirate.complete_say = "Velho Pirata:[ENTER]Ainda coça. Mas me sinto melhor agora. Acho que[ENTER]consigo viver com isso."
gameforge.locale.old_pirate.do_you_get_the_medicine = "I don't have the medicine yet. "
gameforge.locale.old_pirate.do_you_mean_this_medicine = "Here's the cure! "
gameforge.locale.old_pirate.etc_shop_start_say = "Salesperson:[ENTER]The old pirate? Of course I know him! You're[ENTER]talking about this bloke who constantly curses[ENTER]and scratches himself, aren't you? He comes by[ENTER]often to buy some liquid remedy. He spent several[ENTER]thousand Yang on the red medicine. "
gameforge.locale.old_pirate.etc_shop_start_say2 = "General Store Saleswoman:[ENTER]Where on earth does he get so many Yang if he[ENTER]isn't even working? I have already told him a[ENTER]number of times he should let a doctor examine[ENTER]him but he doesn'não me escuta.[ENTER]A cura vermelha apenas alivia os sintomas, mas[ENTER]não't cure the illness. I'm afraid it is not[ENTER]going to end well with him. "
gameforge.locale.old_pirate.etc_shop_talk_about_old_pirate = "Você conhece o Velho Pirata?"
gameforge.locale.old_pirate.order_accept = "I'll help you. "
gameforge.locale.old_pirate.order_accept_answer = "Velho Pirata:[ENTER]Ótimo! Boa sorte![ENTER]O doutor provavelmente está na cidade."
gameforge.locale.old_pirate.order_find_medicine_label = "Remédio para o Velho Pirata"
gameforge.locale.old_pirate.order_find_medicine_text_1 = "Obtenha o remédio para o Velho Pirata."
gameforge.locale.old_pirate.order_find_medicine_text_2 = "Vá até Baek-Go na cidade e peça a ele o[ENTER]remédio para o Velho Pirata."
gameforge.locale.old_pirate.order_refuse = "Sorry, I can't. "
gameforge.locale.old_pirate.order_refuse_answer = "Old Pirate:[ENTER]Well, then I don't have anything left to say to[ENTER]you! "
gameforge.locale.old_pirate.order_say1 = "Old pirate:[ENTER]Damnit, what do you want from me? Crap! The hag[ENTER]from the general store told you about me, didn't[ENTER]she?[ENTER]Yes, everything she said is true. But I don't[ENTER]have time to go see the doctor. I have so many[ENTER]enemies! "
gameforge.locale.old_pirate.order_say2 = "Old pirate:[ENTER]You never know if one of those scoundrels won't[ENTER]just suddenly show up in front of you and stab[ENTER]you in the back! But the itching is so terrible;[ENTER]I can't aguentar mais! Que tal? Você poderia[ENTER]ir ao médico e pegar o remédio? Se você conseguir[ENTER]isso, eu'll give you a great reward. "
gameforge.locale.old_pirate.order_too_late = "Old Pirate:[ENTER]Someone else has already got me the medicine.[ENTER]Well, I'm afraid that means no reward for you! "
gameforge.locale.old_pirate.start_find_old_pirate_label = "Vá até o Velho Pirata."
gameforge.locale.old_pirate.start_find_old_pirate_text_1 = "Vá até o Velho Pirata."
gameforge.locale.old_pirate.start_find_old_pirate_text_2 = "Go to the Old Pirate and ask him why he doesn't[ENTER]want to see a doctor. "
gameforge.locale.old_pirate.start_say = "Old Pirate:[ENTER]Everything itches! I shouldn't have bumped into[ENTER]that guy! We barely touched! Ever since, the spot[ENTER]he touched has been ulcerating, and my whole body[ENTER]itches! It's as if I were rotting while still[ENTER]alive! Bastard! "
gameforge.locale.old_pirate.success_thanks = "Velho Pirata:[ENTER]O quê? Eu fui infectado com a doença misteriosa?![ENTER]E esse é o remédio necessário?![ENTER]Muito, muito obrigado! Você provavelmente[ENTER]salvou minha vida. Eu agradeço."
gameforge.locale.old_pirate.talk = "Você é o Velho Pirata?"
gameforge.locale.old_pirate.talk_about_medicine = "Why don't you go see a doctor? "
