gameforge.locale.questscroll5.mission_12_mis = "Mate 20 Comandantes do Juramento Branco."
gameforge.locale.questscroll5.mission_12_text = "Recentemente, o exército do Juramento Branco tem[ENTER]causado muitos problemas. Eles continuam capturando[ENTER]mascates e causando muitos danos à[ENTER]vila. Por favor, os pare. Elimine os membros[ENTER]do exército e seus líderes."
