gameforge.locale.questscroll5.mission_13_mis = "Mate 25 Jak-To e 25 To-Su da Gangue do Vento Negro.[ENTER]"
gameforge.locale.questscroll5.mission_13_text = "Apparently the Black Wind Gang wants to take over[ENTER]the kingdom. Before they can recruit more troops,[ENTER]we'd better eliminate their leaders.[ENTER]Please could you kill their leaders for us! "
