gameforge.locale.questscroll5.mission_15_mis = "Mate 30 Infantes Selvagens e 30 Servos Selvagens."
gameforge.locale.questscroll5.mission_15_text = "Capturamos um Espião Selvagem e conseguimos obter[ENTER]algumas informações importantes dele. Aparentemente,[ENTER]eles estão planejando um ataque ao reino. Precisamos[ENTER]nos adiantar a eles. Prove sua lealdade ao[ENTER]reino."
