gameforge.locale.questscroll5.mission_16_mis = "Destrua 3 Metins da Escuridão."
gameforge.locale.questscroll5.mission_16_text = "Recentemente, começaram a cair pedras perto da[ENTER]cidade. Elas parecem conter um chi maligno,[ENTER]porque desde então, os animais da área começaram[ENTER]a agir de forma muito estranha. Por favor, cuide[ENTER]deste problema e destrua as pedras. E, é claro,[ENTER]você será recompensado adequadamente pelos seus[ENTER]esforços."
gameforge.locale.questscroll5.mission_17_hunt = "Destrua o Metin Negro"
gameforge.locale.questscroll5.mission_17_mis = "Destrua 2 Metins Negros."
gameforge.locale.questscroll5.mission_17_text = "Recentemente, começaram a cair pedras perto da[ENTER]cidade. Elas parecem conter um chi maligno,[ENTER]porque desde então, os animais da área começaram[ENTER]a agir de forma muito estranha. Por favor, cuide[ENTER]deste problema e destrua as pedras. E, é claro,[ENTER]você será recompensado adequadamente pelos seus[ENTER]esforços."
