gameforge.main_quest_flame_lv100._190_say = "Nessas condições, nem mesmo minha Tintura de Nimbus,[ENTER]que deveria banir a Matéria Negra, não será mais[ENTER]suficiente. Eu preciso urgentemente de[ENTER]ingredientes para fazer mais dela."
gameforge.main_quest_flame_lv100._200_say = {}
