gameforge.main_quest_flame_lv100._300_notice = "Você tem todos os ingredientes necessários. Retorne[ENTER]ao alquimista."
gameforge.main_quest_flame_lv100._310_say = "Fale com o alquimista."
gameforge.main_quest_flame_lv100._320_sendLetter = "Pare a Matéria Negra"
gameforge.main_quest_flame_lv100._330_say = "Que coisa... hmph... Reclamei com o Ferreiro,[ENTER]porque as Pedras Fundamentais foram moídas muito[ENTER]grosseiramente. E agora? Ele está ofendido e se[ENTER]recusa a converter o resto das pedras em pó.[ENTER]A mula teimosa! Você poderia falar com ele, por[ENTER]favor?"
