gameforge.main_quest_flame_lv100._380_say = "Pó de Pedra Fundamental: 1 peça"
gameforge.main_quest_flame_lv100._390_say = "O que posso fazer por você? Devo afiar sua espada? Polir sua armadura?"
gameforge.main_quest_flame_lv100._400_say = "Não, hoje não. Estou aqui porque o alquimista[ENTER]gostaria de pedir seu perdão sobre[ENTER]sua língua afiada mais cedo. Ele enviou a você esta[ENTER]cesta de guloseimas."
gameforge.main_quest_flame_lv100._410_say = "Estou relutante em fazer qualquer coisa por aquele[ENTER]pequeno idiota ingrato. Mas porque é você, vou moer[ENTER]as Pedras Fundamentais. Espere um momento."
gameforge.main_quest_flame_lv100._420_say = "Obrigado!"
gameforge.main_quest_flame_lv100._430_say = "Is the smith being stubborn? Give him the basket[ENTER]and ask him to help. He won't be able to say no[ENTER]to you. "
gameforge.main_quest_flame_lv100._440_notice = "O Pó de Pedra Fundamental está pronto. Leve-o ao[ENTER]alquimista."
gameforge.main_quest_flame_lv100._450_say = "Oh, tão fino quanto Pó de Bebê, excelente! Mas o velho é cabeça dura, hehe. Agora posso[ENTER]misturar a tintura Nimbus. Descanse um pouco.[ENTER]Eu chamarei você quando estiver pronto."
gameforge.main_quest_flame_lv101 = {}
