gameforge.main_quest_flame_lv101._320_say = {}
gameforge.main_quest_flame_lv101._330_say = "Os caracteres na parte interna do Colar[ENTER]Rebitado são de uma cultura antiga, que[ENTER]existiu há muito tempo. Para decifrá-los, preciso[ENTER]do Sangue do Rei Chama. Ele flui por suas veias como[ENTER]lava."
gameforge.main_quest_flame_lv101._340_say = "Considere feito."
