gameforge.main_quest_flame_lv101._380_notice = "Você recebeu Sangue do Rei Chamas.[ENTER]Retorne para Yon-I-Walker."
gameforge.main_quest_flame_lv101._390_say = "Fale com Yon-I-Walker."
