gameforge.main_quest_lv12._110_say = "Você quer desistir desta missão?"
gameforge.main_quest_lv12._120_say = "So you made it? Well done! You did not have too[ENTER]much trouble, I hope? Anyway, let's move on to[ENTER]the next step. "
gameforge.main_quest_lv12._130_targetVid = "Encontre o Capitão"
gameforge.main_quest_lv12._140_sendLetter = "Tarefa concluída"
gameforge.main_quest_lv12._150_say = "Tarefa concluída"
gameforge.main_quest_lv12._160_sayReward = "Você matou o número necessário de Lobos Cinzentos\nAlfa. Agora volte ao Capitão e relate\nseu sucesso."
gameforge.main_quest_lv12._170_say = "Did you kill the Alpha Grey Wolves? Good work.[ENTER]Thanks to you, it's a lot easier to keep the[ENTER]village safe now. That's really good news for a[ENTER]change in times of war like these. "
gameforge.main_quest_lv12._180_sayReward = "Você recebeu 40.000 pontos de EXP.[ENTER]Você recebeu 10.000 Yang.[ENTER]Você recebeu um Livro de Habilidade."
gameforge.main_quest_lv12._20_say = "Vários Lobos Alfa Cinzentos estão causando[ENTER]caos bem na saída da vila. Eles estão[ENTER]correndo soltos há um tempo, mas agora o comportamento[ENTER]deles está ficando completamente fora de controle! Eles[ENTER]começaram a atacar a vila e as pessoas nas[ENTER]ruas. Para retomar o controle da situação,[ENTER]temos que matar alguns Lobos Alfa Cinzentos. Você poderia[ENTER]ajudar? Elimine 10 Lobos Alfa Cinzentos para nós."
gameforge.main_quest_lv12._30_sayReward = "Você receberá 24.000 pontos de EXP e[ENTER]3.500 Yang como recompensa."
gameforge.main_quest_lv12._40_sendLetter = "Mate 10 Lobos Alfa Cinzentos"
gameforge.main_quest_lv12._50_sayReward = "Os Lobos Alfa Cinzentos estão atacando a vila.[ENTER]Para controlar a situação, você tem que[ENTER]matar 10 deles."
gameforge.main_quest_lv12._60_sayReward = "Até agora, você matou %s Lobos Alfa Cinzentos."
gameforge.main_quest_lv12._65_counter = "Restantes"
gameforge.main_quest_lv12._70_sayReward = "Você matou todos os 10 Lobos Alfa Cinzentos. Retorne[ENTER]ao Capitão."
gameforge.main_quest_lv12._80_npcChat = "Mate Lobos Alfa Cinzentos"
gameforge.main_quest_lv12._90_say = "What, you still haven't finished the task?[ENTER]Are you having trouble killing 10 Alpha Grey[ENTER]Wolves? If you want to keep fighting for your[ENTER]kingdom you should keep trying. "
