gameforge.main_quest_lv90._360_say = "One of the following creatures has Lee Chung's[ENTER]Letter: Gnoll Warrior, Gnoll Archer, Gnoll[ENTER]Defender, Gnoll Magician, Gnoll General, Gnoll[ENTER]Lord or the Supreme Gnoll Guard "
gameforge.main_quest_lv90._370_say = "Se quisermos parar Sung Mahi, precisamos desta carta! O ladrão vem das fileiras da tribo gnoll."
