gameforge.main_quest_lv90._420_say = "Meus batedores descobrirão se os gnolls já[ENTER]receberam a carta ou não. Enquanto isso,[ENTER]aceite isto como um agradecimento."
gameforge.main_quest_lv90._430_sendLetter = "A Carta dos Gnolls I"
gameforge.main_quest_lv90._440_say = "O Almirante Angmur está procurando por você. Descubra o que[ENTER]ele quer."
gameforge.main_quest_lv90._450_say = "O General Gnoll já recebeu a carta[ENTER]enviada pelos Servos. Você pode obtê-la para nós?[ENTER]Tenha cuidado, porém: gnolls são extremamente[ENTER]agressivos.[ENTER]Eles atacam primeiro e perguntam depois."
gameforge.main_quest_lv90._460_say = "I'm already on my way. "
gameforge.main_quest_lv90._470_say = "Obrigado. Eu sabia que poderíamos contar com você."
gameforge.main_quest_lv90._480_sayTitle = "Encontre a Carta dos Gnolls!"
gameforge.main_quest_lv90._490_say = "Encontre o General Gnoll. Ele tem a carta!"
gameforge.main_quest_lv90._500_say = "Não temos tempo a perder. Por favor, apresse-se e traga-me[ENTER]a carta."
