gameforge.new_quest_lv42._026_select = "I'm off to find the fisherman! "
gameforge.new_quest_lv42._030_target = "Pescador:"
gameforge.new_quest_lv42._031_say = "The untroubled days at sea are over! Fishermen[ENTER]are disappearing every day, it's terrible! Ever[ENTER]since the Metin stones plunged into the sea, the[ENTER]once peaceful whales have transformed into brutal[ENTER]sea monsters. Fishing has become a dangerous[ENTER]trade. "
gameforge.new_quest_lv42._032_say = "Our spears can't hurt the whales any more. Their[ENTER]skin is tougher than ogre skin! "
gameforge.new_quest_lv42._033_say = "Talvez Yonah saiba o que fazer. Ele costumava ir ao[ENTER]mar com frequência e caçou baleias por anos."
gameforge.new_quest_lv42._034_say_title = "Pescador:"
gameforge.new_quest_lv42._035_say = "Octavio está esperando por seu peixe de águas[ENTER]profundas. Pergunte ao Pescador por que a entrega está atrasada."
gameforge.new_quest_lv42._036_say = "O Pescador está em %s."
gameforge.new_quest_lv42._037_send_letter = "Monstros Marinhos Furiosos"
gameforge.new_quest_lv42._038_say = "O Pescador pediu a você para pedir conselhos a[ENTER]Yonah sobre como caçar as baleias malignas."
gameforge.new_quest_lv42._039_say = "Yonah está em %s."
gameforge.new_quest_lv42._040_target = "Yonah:"
gameforge.new_quest_lv42._041_say = "I heard about what happened to the whales. It's a[ENTER]bad business for fishermen. Even if they dare to[ENTER]venture out onto the sea, their spears are just[ENTER]too weak to penetrate whale skin. When I used to[ENTER]work on a whaler, we used special weapons for[ENTER]whales with old and scarred skin. They were able[ENTER]to penetrate everything... "
gameforge.new_quest_lv42._042_say = "It's a special technique to harden whale catching[ENTER]spears. The only person who can make this type of[ENTER]spear is a Blacksmith. "
gameforge.new_quest_lv42._043_say_reward = "Vá para o Ferreiro"
gameforge.new_quest_lv42._044_say_title = "Yonah:"
gameforge.new_quest_lv42._045_say = "Yonah recomenda usar lanças muito robustas e[ENTER]afiadas para caçar baleias. Vá para o Ferreiro[ENTER]e pergunte a ele se ele pode fazer algumas dessas lanças."
gameforge.new_quest_lv42._050_target = "Ferreiro"
gameforge.new_quest_lv42._051_say = "Yes, I've heard of those spears and I can also[ENTER]make them. However, so that they can gain the[ENTER]required durability, I do need a Diamond Stone. "
gameforge.new_quest_lv42._052_say = "Eu preciso:"
gameforge.new_quest_lv42._053_say_reward = "Processo:"
gameforge.new_quest_lv42._054_say_title = "Ferreiro"
gameforge.new_quest_lv42._060_set_title = "Pedra Diamante"
gameforge.new_quest_lv42._061_notice = "Let's see. I need a Pickaxe to mine the stone. I[ENTER]wonder whether mine is around here somewhere or[ENTER]whether Deokbae has to get me a new one? "
gameforge.new_quest_lv42._062_say = "Você precisa de mais %s x Pedra Diamante."
gameforge.new_quest_lv42._063_say = "Você tem tudo de que precisa. Agora vá[ENTER]rapidamente de volta ao Ferreiro."
gameforge.new_quest_lv42._064_say = "Great, now I've got everything that I need. Come[ENTER]back at the same time tomorrow to pick up the[ENTER]spears. Iron like that has to be forged[ENTER]carefully. "
gameforge.new_quest_lv42._065_say = "Você pode obter uma Pedra Diamante lascando a[ENTER]Jazida de Minério de Diamante com uma Picareta."
gameforge.new_quest_lv42._066_say = "The Blacksmith needs a Diamond Stone to make[ENTER]special spears for hunting whales. Look for Veins[ENTER]of Diamond Ore, so that you can mine Diamond[ENTER]Stone. If you don't have a Pickaxe for mining,[ENTER]you can buy one from Deokbae. "
gameforge.new_quest_lv42._067_say = "O Ferreiro está em %s."
gameforge.new_quest_lv42._068_say = "Precisamos de mais %s x Pedra Diamante antes que[ENTER]possamos começar a trabalhar nas lanças."
gameforge.new_quest_lv42._070_set_title = "As lanças"
gameforge.new_quest_lv42._071_set_clock = "Tempo de espera"
gameforge.new_quest_lv42._072_say = "O Ferreiro já deve ter terminado. Vá até[ENTER]ele e pegue as lanças especiais para caçar[ENTER]baleias."
gameforge.new_quest_lv42._073_say = "The spears aren't finished yet. You have to be a[ENTER]little more patient. "
gameforge.new_quest_lv42._074_say = "Aqui estão suas lanças. Leve-as ao Pescador."
gameforge.new_quest_lv42._075_say = "I've already asked you to wait until the spears[ENTER]are finished. Come back later. "
gameforge.new_quest_lv42._079_say = "Você recebeu algumas Lanças de Baleia do[ENTER]Ferreiro. Leve-as ao Pescador."
gameforge.new_quest_lv42._080_say = "Great! These spears are really impressive! We can[ENTER]protect ourselves from whale attacks now. Please[ENTER]let Octavio know that he'll have fresh fish again[ENTER]soon! "
gameforge.new_quest_lv42._081_say_reward = "Você recebeu 500.000 Yang."
gameforge.new_quest_lv42._082_say_reward = "Como um reconhecimento especial, você recebeu 3x:"
gameforge.new_quest_lv42._083_say = "Those aren't enough spears to drive a whale away.[ENTER]It's just enough to tickle their flesh. "
gameforge.new_quest_lv42._084_say_reward = "Por favor, vá de volta ao Ferreiro novamente e[ENTER]peça para ele forjar mais lanças."
gameforge.new_quest_lv42._090_say = "Os pescadores estão bem equipados e podem caçar no[ENTER]mar aberto novamente agora. Vá até Octavio e tente[ENTER]assegurá-lo de que ele receberá uma nova entrega em breve."
gameforge.new_quest_lv42._095_say = "What did you say? Peaceful whales have become[ENTER]aggressive sea monsters? That's terrible...[ENTER]Thanks for helping the fishermen. A large part of[ENTER]my menu is dependant on their deliveries. "
gameforge.new_quest_lv42._096_say = "Oh and about your grumbling stomach: Of course I[ENTER]can offer you seafood stew, but wouldn't you[ENTER]prefer some sushi? "
gameforge.new_quest_lv42._098_say_reward = "Você recebe 750.000 pontos de EXP.[ENTER]Você recebe 2 pedaços de Sushi."
