gameforge.new_quest_lv54._073_say = "Particularmente o Tigre Branco Amaldiçoado se[ENTER]multiplicou incontrolavelmente."
gameforge.new_quest_lv54._074_say = "Como prova, eu preciso de:"
gameforge.new_quest_lv54._075_say = "Você deve caçar:"
gameforge.new_quest_lv54._076_say = "Procure por Yang-Shin e tente convencê-lo a[ENTER]proteger as rotas comerciais contra os tigres."
gameforge.new_quest_lv54._077_say_title = "Yang-Shin:"
gameforge.new_quest_lv54._078_say = "Yang-Shin está em %s."
gameforge.new_quest_lv54._080_set_title = "A Pele de Tigre"
gameforge.new_quest_lv54._081_notice = "Ah, um espécime esplêndido. Só mais %s deles!"
gameforge.new_quest_lv54._082_notice = "Pronto! Agora volte rapidamente para Yang-Shin."
