gameforge.new_quest_lv54._096_say = "Recompensa:"
gameforge.new_quest_lv54._097_say_reward = "Você recebeu um Escudo de Tigre+3."
gameforge.new_quest_lv54._098_say = "Você quer me enrolar com algumas peles? Desse jeito[ENTER]eu vou quitar minhas dívidas... Quer dizer, desse jeito as ruas[ENTER]nunca estarão seguras o suficiente!"
