gameforge.subquest_08._270_sayReward = "Yu-Rang is always being teased by Taurean. Punish[ENTER]him and then she'll give you a Rice Cake. "
gameforge.subquest_08._280_npcChat = "Por que você está provocando Yu-Rang?"
gameforge.subquest_08._290_say = "What, who am I supposed to have annoyed? Oh, you[ENTER]mean the girl who sells Rice Cakes? No, I didn't[ENTER]want to annoy her! I was just bored and she[ENTER]looked nice and like she didn'tinha muito o que fazer.[ENTER]Então eu a visitei algumas vezes e queria brincar[ENTER]com ela, mas então ela ficou brava...[ENTER]Garotas são estranhas. Eu suponho que não't visit her[ENTER]any more then! "
gameforge.subquest_08._300_makequestbutton = "Volte para Yu-Rang"
gameforge.subquest_08._30_select = "Aceitar"
gameforge.subquest_08._310_targetVid = "Bolinho de Arroz"
gameforge.subquest_08._320_sayReward = "Taurean won't bother Yu-Rang any more. Go to her[ENTER]and pick up your Rice Cake. "
gameforge.subquest_08._330_say = "Ha, ha! You punished Taurean! Thank you. Now[ENTER]he'll think twice before he comes back here[ENTER]again. Here's your Rice Cake. Enjoy your meal! "
gameforge.subquest_08._340_makequestbutton = "Volte para Ah-Yu"
gameforge.subquest_08._350_targetVid = "Bolo de Arroz para Ah-Yu"
gameforge.subquest_08._360_sayReward = "You have received a Rice Cake from Yu-Rang. Take[ENTER]it to Ah-Yu. You'll find her and her child in the[ENTER]first village. "
gameforge.subquest_08._370_npcChat = "Here's the Rice Cake for your child. "
gameforge.subquest_08._380_say = "Você me trouxe o Bolo de Arroz! Muito obrigada.[ENTER]Oh! Veja, meu bebê parou de chorar agora[ENTER]e está feliz comendo o bolo. Você conseguiu.[ENTER]Obrigada pela sua ajuda!"
