gameforge.subquest_08._40_say = "But... I can't leave this place and the baby is[ENTER]terrorising the whole neighbourhood... It won'posso[ENTER]demorar! Você tem certeza de que não't have[ENTER]enough time? "
gameforge.subquest_08._50_say = "I guess you're busy?[ENTER]Please come back later.[ENTER]Don't cry, my child. "
gameforge.subquest_08._60_say = "I guess I'll have to ask someone else.[ENTER]Good luck on your journey! "
gameforge.subquest_08._70_say = "Thanks a lot! That's a real relief to me. "
gameforge.subquest_08._80_makequestbutton = "Bolo de Arroz"
gameforge.subquest_08._90_targetVid = "Bolo de Arroz"
