gameforge.subquest_21._70_say = "Você quer parar de procurá-lo?"
gameforge.subquest_21._80_say = "Well, that's always happening to me. Everywhere I[ENTER]look there are people who don't like me. If you[ENTER]change your mind, come back again later. "
gameforge.subquest_21._90_say = "Go! And don't ever come here again! It's always[ENTER]the same... "
