gameforge.subquest_27._190_qSetTitle = "Vá até Soon"
gameforge.subquest_27._200_targetVid = "Soon novamente"
gameforge.subquest_27._20_npcChat = "A alegria de ler"
gameforge.subquest_27._210_say = "Vá até Soon[ENTER]Vá até Soon e pergunte a ele sobre o Livro do Templo."
gameforge.subquest_27._220_npcChat = "O que é o Livro do Templo Secreto?"
gameforge.subquest_27._230_say = "You know, I have read every book in the world.[ENTER]Recently, I've been hearing about a Secret Temple[ENTER]Book. Rumour has it that this book contains all[ENTER]the secrets to the Temple of Darkness. It also[ENTER]explains how you can become a saint. "
