gameforge.subquest_27._250_select = "I'll do it! "
gameforge.subquest_27._260_select = "I can't, sorry. "
gameforge.subquest_27._270_say = "Você quer desistir desta missão?"
gameforge.subquest_27._280_say = "Come on, don't you want to help me? If not now,[ENTER]then come back later. I'll be waiting for you. "
gameforge.subquest_27._290_say = "You really don't want to help me? You need[ENTER]holiday, huh? The book is really important to me.[ENTER]What do I do now? Damn. "
gameforge.subquest_27._300_say = "Thanks, I'll be waiting for you. "
gameforge.subquest_27._30_say = "Do you like reading as much as I do?[ENTER]Words that join into sentences and are finally[ENTER]unified in a book. How wonderful! And the pure[ENTER]joy of writing a book so that other people can[ENTER]read it! It doesn't get any better than that! "
gameforge.subquest_27._310_makequestbutton = "Você encontrou o Livro do Templo Secreto."
gameforge.subquest_27._320_qSetTitle = "Você encontrou o Livro do Templo Secreto. Agora retorne[ENTER]para Soon."
gameforge.subquest_27._330_targetVid = "Retorne para Soon."
gameforge.subquest_27._340_makequestbutton = "Derrote os Fanáticos Negros"
gameforge.subquest_27._350_sayTitle = "Você matou os Fanáticos Negros."
gameforge.subquest_27._360_say = "Leve o Livro do Templo Secreto para Soon."
gameforge.subquest_27._370_say = "Soon tem um profundo interesse no Livro do Templo[ENTER]Secreto. Se você matar os Fanáticos Negros, você pode[ENTER]encontrar o Livro do Templo Secreto."
gameforge.subquest_27._380_npcChat = "O Livro do Templo Secreto"
gameforge.subquest_27._390_say = "Oh, that's it! Many thanks![ENTER]Here is your reward.[ENTER]Now leave me alone, I have to read! "
gameforge.subquest_27._400_sayReward = "Você recebeu 850.000 de EXP."
gameforge.subquest_27._40_say = "Life is pulsing between the pages of this book![ENTER]Young knowledge and old wisdom live here![ENTER]Imagine how bleak my life would be without[ENTER]books... Only unfortunately... my wife doesn't[ENTER]understand it. Could you try convincing her that[ENTER]it's a good thing? "
gameforge.subquest_27._410_sayReward = "Você recebeu 20.000 Yang."
gameforge.subquest_27._420_say = "Hey... You haven't got it yet? If you start a[ENTER]quest, you should see it through until the end.[ENTER]Now finish the quest! "
gameforge.subquest_27._430_say = "Os Fanáticos Sombrios não devem ser subestimados.[ENTER]Tente novamente mais tarde."
gameforge.subquest_27._440_say = "Bem, então terei que pedir ajuda a outra pessoa.[ENTER]Adeus."
gameforge.subquest_27._450_say = "Thanks, I'll be waiting for you. I can't wait to[ENTER]hold it in my hands and read it. "
gameforge.subquest_27._50_makequestbutton = "Vá para Aranyo"
gameforge.subquest_27._60_sayTitle = "Vá para Aranyo"
gameforge.subquest_27._70_say = "Vá para Aranyo e pergunte sobre o marido dela."
gameforge.subquest_27._80_npcChat = "Por que você está tão bravo?"
gameforge.subquest_27._90_say = "Angry? I'm about to explode with anger! Does my[ENTER]husband to out to work? No. Does he ever bring[ENTER]Yang home? No. Instead he's hiding away somewhere[ENTER]between the pages of a book! Have you ever heard[ENTER]anything like it? Can a book bring us clothing?[ENTER]Does it provide us with food? "
