gameforge.subquest_33._370_sayTitle = "Voltar para Soon"
gameforge.subquest_33._380_say = "Você estava cavando em um lugar que o Mapa dos Deuses[ENTER]Animais indicava e realmente encontrou um Baú do Tesouro! Vá[ENTER]até Soon e conte a ele sobre o resultado inesperado."
gameforge.subquest_33._390_npcChat = "Eu encontrei o tesouro!"
gameforge.subquest_33._400_say = "Oh! This is a surprise! Let's see... Amazing.[ENTER]This is exactly how the treasure chest was[ENTER]described in the old texts. Now I only have to[ENTER]find out how to open it. But that is not your[ENTER]problem. Thanks! Here, take this as a token of my[ENTER]gratitude! "
gameforge.subquest_33._40_say = "A ancient fairy tale says that these four godlike[ENTER]monsters divided the map into four parts and[ENTER]protected these territories. I'm not sure if[ENTER]that'está certo. Você poderia verificar isso para mim? Ah,[ENTER]por favor, essa incerteza está me matando... Eu'm[ENTER]begging you, please help me! "
gameforge.subquest_33._410_sayReward = "Você recebe 1,400,000 EXP."
gameforge.subquest_33._420_sayReward = "Você recebeu 25,000 Yang."
gameforge.subquest_33._430_sayReward = "Você recebeu um Elmo Máscara Fantasma+1."
gameforge.subquest_33._440_sayReward = "Você recebeu um Elmo de Castelo+1."
gameforge.subquest_33._450_sayReward = "Você recebeu um Capuz de Aço+1."
gameforge.subquest_33._460_sayReward = "Você recebeu um Chapéu de Luz Solar+1."
gameforge.subquest_33._470_say = "Well, where is he? Do you want to make fun of[ENTER]me?! You haven't really found him, have you? "
gameforge.subquest_33._50_say = "You're not interested in old Treasure Maps? You[ENTER]are strange... I'm going to carry on looking for[ENTER]clues in these texts. Come back if you change[ENTER]your mind. "
gameforge.subquest_33._60_say = "You don't like those kinds of adventures, do you?[ENTER]How am I supposed to get the pieces of the map?[ENTER]Ah well... Farewell "
gameforge.subquest_33._70_say = "You are a true friend. I believe that you'll find[ENTER]them. "
gameforge.subquest_33._80_sendLetter = "The bookworm's Treasure Map "
gameforge.subquest_33._90_targetVid = "Vá ao rato de biblioteca"
