gameforge.subquest_38._100_say = "The hunter's Lavishly Decorated Bow, which is a[ENTER]family heirloom, has been stolen from him. Go to[ENTER]Sangsun Dong and get back the Decorated Bow from[ENTER]the thieving monkeys. "
gameforge.subquest_38._10_targetVid = "The Hunter's Treasure "
gameforge.subquest_38._110_sendLetter = "Encontrou o Arco Luxuosamente Decorado"
gameforge.subquest_38._120_targetVid = "Vá para o caçador Yang-Shin"
gameforge.subquest_38._125_say = "Você encontrou o Arco Luxuosamente Decorado do[ENTER]Caçador. Leve-o de volta para Yang-Shin."
gameforge.subquest_38._130_npcChat = "Here's the bow! "
gameforge.subquest_38._140_say = "Wow, that's it! My bow, yay! My honour has been[ENTER]restored. Phew. I'tenho sofrido por tanto tempo[ENTER]desde que o perdi... Eu'm really grateful for your[ENTER]help. It'é uma honra para mim dar a você isto[ENTER]aqui. Pode não parecer muito, mas é's from[ENTER]the heart so take it. Thanks to you, I can carry[ENTER]on my life in peace! "
gameforge.subquest_38._150_sayReward = "Você recebe 1.500.000 pontos de experiência."
gameforge.subquest_38._160_sayReward = "Você recebeu 35.000 Yang."
gameforge.subquest_38._170_sayReward = "Você recebeu uma Capa da Bravura."
gameforge.subquest_38._180_say = "Você encontrou meu Arco? Mas onde ele está? Você[ENTER]perdeu de novo?"
gameforge.subquest_38._190_select = "I'll keep looking. "
gameforge.subquest_38._200_say = "Que assim seja.[ENTER]Cuide-se."
gameforge.subquest_38._20_say = "Hey, could you listen to me for a second? As you[ENTER]can see, I'm a hunter; my family has been hunting[ENTER]for generations. We're all hunters, a family with[ENTER]so much honour. But I made a big mistake that is[ENTER]going to dishonour me in front of my whole[ENTER]family... "
gameforge.subquest_38._210_say = "Você poderia tentar novamente? Obrigado.[ENTER]Com suas habilidades, você deve conseguir fazer isso."
gameforge.subquest_38._30_say = "É sobre um Arco Luxuosamente Decorado que foi[ENTER]roubado por macacos um tempo atrás. Eu estava caçando na[ENTER]Caverna dos Macacos na época e fiquei apenas[ENTER]desatento por um segundo - eles se aproveitaram da[ENTER]situação e o roubaram de mim."
gameforge.subquest_38._40_say = "I have never been into the depths of the Sangsun[ENTER]Dong, so I don't really know my way around there[ENTER]and need your help. A few locals told me that you[ENTER]are very talented. That'é por isso que pedi este favor a você.[ENTER]Eu posso't face my ancestors without my[ENTER]Decorated Bow. "
gameforge.subquest_38._50_sayTitle = "Yang-Shin:"
gameforge.subquest_38._60_say = "Oh, preciso dele de volta o mais rápido possível...[ENTER]Por favor, volte quando tiver um tempo.[ENTER]Adeus."
gameforge.subquest_38._70_say = "Bem então...[ENTER]Cuidado em suas viagens."
gameforge.subquest_38._80_say = "You can find it easily, because it's a richly[ENTER]ornamented bow. I will give you a great reward[ENTER]for it! "
gameforge.subquest_38._90_sayTitle = "Procure pelo Arco Luxuosamente Decorado"
