gameforge.subquest_41._70_say = "Hm, onde vou conseguir esses olhos?[ENTER]Certo, até breve."
gameforge.subquest_41._80_say = "Thank you. I knew you'd do me this favour. I'll[ENTER]treat you to a really nice meal for doing this. "
gameforge.subquest_41._90_sayTitle = "Retorne a Octávio"
