gameforge.subquest_42._220_sayReward = "Você recebeu 3 Diamantes."
gameforge.subquest_42._230_say = "Isso foi ótimo. Agora podemos acalmar os soldados[ENTER]mortos. Eles deram o melhor de si. Agora, eles podem[ENTER]descansar em paz. Deixe-me dar a você uma recompensa[ENTER]por essa nobre ação."
