gameforge.subquest_44._340_sayReward = "Você recebeu 30.000 Yang."
gameforge.subquest_44._350_sayReward = "Você recebeu 350.000 EXP."
gameforge.subquest_44._360_sendLetter = "Sino para a aranha"
gameforge.subquest_44._370_say = "Go to the desert to the Baby Spiders and try to[ENTER]hang Taurean's bell on one of them. "
