gameforge.subquest_44._390_say = "You haven't hung the bell onto the spider yet?[ENTER]Then please do it soon. And make sure Yu-Rang[ENTER]doesn't catch you! "
gameforge.subquest_44._400_sendLetter = "Feito!"
