gameforge.subquest_44._440_npcChat = "O Taurean disse a verdade."
gameforge.subquest_44._450_say = "Come again? You have found a spider with a bell?[ENTER]Then Taurean really did it. I would have not[ENTER]thought it possible. Perhaps he will be a real[ENTER]man one day after all. Thank you for checking[ENTER]that for me. Here's a reward for your help. "
gameforge.subquest_44._460_sendLetter = "Voltar para o Taurean"
gameforge.subquest_44._470_targetVid = "Voltar para o Taurean"
gameforge.subquest_44._480_say = "Você disse à Yu-Rang que o amigo dela realmente[ENTER]pendurou um sino em uma Aranha Filhote.[ENTER]Agora volte para o Taurean e conte a ele sobre a[ENTER]enganação bem-sucedida."
